  2	import { readFileSync } from 'fs';
  3	import { readJsonSync } from 'fs-extra';
  4	import { codeBlock, h1, h2, h3, lines } from 'markdown-factory';
  5	import { join } from 'path';
  6	import { register as registerTsConfigPaths } from 'tsconfig-paths';
  7	
  8	import { examples } from '../../../packages/nx/src/command-line/examples';
  9	import {
 10	  formatDescription,
 11	  generateMarkdownFile,
 12	  generateOptionsMarkdown,
 13	  getCommands,
 14	  parseCommand,
 15	  ParsedCommand,
 16	} from '../utils';
