 18	const importFresh = require('import-fresh');
 19	
 20	// Docs for these commands are inside docs/shared/cli - they are not dynamically generated.
 21	const sharedCommands = ['generate', 'exec'];
 22	
 23	// These commands are hidden from the documentation.
 24	const hiddenCommands = [
 25	  '$0',
 26	  // These commands are only applicable to powerpack users
 27	  // TODO: Introduce custom formatting for such commands, instead of hiding them
 28	  'conformance',
 29	  'conformance:check',
 30	];
 31	
 32	export async function generateCliDocumentation(
 33	  commandsOutputDirectory: string
 34	) {
 35	  /**
 36	   * For certain commands, they will output dynamic data at runtime in a real workspace,
 37	   * so we leverage an envrionment variable to inform the logic of the context that we
 38	   * are just statically generating documentation for the current execution.
 39	   */
 40	  process.env.NX_GENERATE_DOCS_PROCESS = 'true';
 41	
 42	  const config = readJsonSync(
 43	    join(__dirname, '../../../tsconfig.base.json')
 44	  ).compilerOptions;
 45	  registerTsConfigPaths(config);
 46	
 47	  console.log(`\n${chalk.blue('i')} Generating Documentation for Nx Commands`);
 48	
 49	  const { commandsObject } = importFresh(
 50	    '../../../packages/nx/src/command-line/nx-commands'
 51	  );
 52	
 53	  function generateMarkdown(command: ParsedCommand) {
 54	    let templateLines = [
 55	      `
 56	---
 57	title: "${command.name} - CLI command"
 58	description: "${command.description}"
 59	---`,
 60	      h1(command.name),
 61	      formatDescription(command.description, command.deprecated),
 62	      h2('Usage'),
 63	      codeBlock(`nx ${command.commandString}`, 'shell'),
 64	      'Install `nx` globally to invoke the command directly using `nx`, or use `npx nx`, `yarn nx`, or `pnpm nx`.',
 65	    ];
 66	
 67	    if (examples[command.name] && examples[command.name].length > 0) {
 68	      templateLines.push(h3('Examples'));
 69	      examples[command.name].forEach((example) => {
 70	        templateLines.push(
 71	          example.description + ':',
 72	          codeBlock(` nx ${example.command}`, 'shell')
 73	        );
 74	      });
 75	    }
 76	
 77	    templateLines.push(generateOptionsMarkdown(command));
 78	
 79	    if (command.subcommands?.length) {
 80	      templateLines.push(h2('Subcommands'));
 81	      for (const subcommand of command.subcommands) {
 82	        templateLines.push(
 83	          h3(subcommand.name.replace('$0', 'Base Command Options')),
 84	          formatDescription(subcommand.description, subcommand.deprecated),
 85	          codeBlock(
 86	            `nx ${command.commandString} ${subcommand.commandString.replace(
 87	              '$0 ',
 88	              ''
 89	            )}`,
 90	            'shell'
 91	          ),
 92	          generateOptionsMarkdown(subcommand, 2)
 93	        );
 94	      }
 95	    }
 96	
 97	    return {
 98	      name: command.name
 99	        .replace(':', '-')
100	        .replace(' ', '-')
101	        .replace(/[\]\[.]+/gm, ''),
102	      template: lines(templateLines),
103	    };
104	  }
105	
106	  // TODO: Try to add option's type, examples, and group?
107	  const nxCommands = getCommands(commandsObject);
108	  await Promise.all(
109	    Object.keys(nxCommands)
110	      .filter(
111	        (name) =>
112	          !sharedCommands.includes(name) &&
113	          !hiddenCommands.includes(name) &&
114	          // These are all supported yargs fields for description
115	          (nxCommands[name].description ||
116	            nxCommands[name].describe ||
117	            nxCommands[name].desc)
118	      )
119	      .map((name) => parseCommand(name, nxCommands[name]))
120	      .map(async (command) => generateMarkdown(await command))
121	      .map(async (templateObject) =>
122	        generateMarkdownFile(commandsOutputDirectory, await templateObject)
123	      )
124	  );
125	
126	  await Promise.all(
127	    sharedCommands.map((command) => {
128	      const sharedCommandsDirectory = join(
129	        __dirname,
130	        '../../../docs/shared/cli'
131	      );
132	      const sharedCommandsOutputDirectory = join(
133	        __dirname,
134	        '../../../docs/',
135	        'generated',
136	        'cli'
137	      );
138	
139	      const templateObject = {
140	        name: command,
141	        template: readFileSync(
142	          join(sharedCommandsDirectory, `${command}.md`),
143	          'utf-8'
144	        ),
145	      };
146	
147	      return generateMarkdownFile(
148	        sharedCommandsOutputDirectory,
149	        templateObject
150	      );
151	    })
152	  );
153	
154	  delete process.env.NX_GENERATE_DOCS_PROCESS;
155	
156	  console.log(`${chalk.green('✓')} Generated Documentation for Nx Commands`);
157	}
158

