import type { CommandModule } from 'yargs';

export interface McpOptions {
  port?: number;
  host?: string;
  verbose?: boolean;
}

export const yargsMcpCommand: CommandModule<{}, McpOptions> = {
  command: 'mcp',
  describe: 'Start the Nx MCP (Model Context Protocol) server for AI assistants',
  builder: (yargs) =>
    yargs
      .option('port', {
        type: 'number',
        description: 'Port to run the MCP server on',
        default: 3333,
      })
      .option('host', {
        type: 'string',
        description: 'Host to bind the MCP server to',
        default: 'localhost',
      })
      .option('verbose', {
        type: 'boolean',
        description: 'Enable verbose logging',
        default: false,
      })
      .example(
        '$0 mcp',
        'Start the MCP server with default settings'
      )
      .example(
        '$0 mcp --port 4444',
        'Start the MCP server on port 4444'
      )
      .example(
        '$0 mcp --verbose',
        'Start the MCP server with verbose logging'
      ),
  handler: async (args) => {
    const { mcpHandler } = await import('./command');
    await mcpHandler(args);
  },
};

