import type { CommandModule } from 'yargs';

export interface McpOptions {
  port?: number;
  host?: string;
  verbose?: boolean;
}

export const yargsMcpCommand: CommandModule<{}, McpOptions> = {
  command: 'mcp',
  describe: 'Start the Nx MCP (Model Context Protocol) server for AI assistants',
  builder: (yargs) =>
    yargs
      .option('port', {
