import { output } from '../../utils/output';
import { getWorkspaceLayout, getProjects } from '../../config/configuration';
import { workspaceRoot } from '../../utils/workspace-root';
import type { McpOptions } from './command-object';

export async function mcpHandler(options: McpOptions): Promise<void> {
  const { verbose, port, host } = options;

  try {
    output.log({
      title: 'Starting Nx MCP Server',
      bodyLines: [
        `Host: ${host}`,
        `Port: ${port}`,
        `Workspace: ${workspaceRoot}`,
        verbose ? 'Verbose logging: enabled' : '',
      ].filter(Boolean),
    });

    // Import the MCP server implementation
    const { NxMcpServerWrapper } = await import('../../mcp');

    // Create a basic workspace info provider
    const workspaceInfoProvider = {
      async nxWorkspace() {
        try {
          const projects = getProjects();
          return {
            workspaceLayout: getWorkspaceLayout(),
            projects: Array.from(projects.entries()).map(([name, config]) => ({
              name,
              ...config,
            })),
          };
        } catch (error) {
          if (verbose) {
            output.error({
              title: 'Error loading workspace',
              bodyLines: [error.message],
            });
          }
          return undefined;
        }
      },
      async getGenerators() {
        // TODO: Implement generators listing
        return [];
      },
      async getGitDiffs() {
        // TODO: Implement git diffs
        return null;
      },
      async isNxCloudEnabled() {
        // TODO: Check for Nx Cloud configuration
        return false;
      },
    };

    // Create and start the MCP server
    const mcpServer = await NxMcpServerWrapper.create(
      workspaceRoot,
      workspaceInfoProvider
    );

    output.success({
      title: 'Nx MCP Server Started',
      bodyLines: [
        'The MCP server is now running and ready to serve Nx workspace information.',
        'You can now connect AI assistants that support the Model Context Protocol.',
        '',
        'Press Ctrl+C to stop the server.',
      ],
    });

    // Handle graceful shutdown
    process.on('SIGINT', () => {
      output.log({
        title: 'Shutting down Nx MCP Server',
        bodyLines: ['Goodbye!'],
      });
      process.exit(0);
    });

    process.on('SIGTERM', () => {
      output.log({
        title: 'Shutting down Nx MCP Server',
        bodyLines: ['Goodbye!'],
      });
      process.exit(0);
    });

    // Keep the process running
    await new Promise(() => {});
  } catch (error) {
    output.error({
      title: 'Failed to start Nx MCP Server',
      bodyLines: [error.message],
    });
    process.exit(1);
  }
}

