import { output } from '../../utils/output';
import { workspaceRoot } from '../../utils/workspace-root';
import type { McpOptions } from './command-object';

export async function mcpHandler(options: McpOptions): Promise<void> {
  const { verbose } = options;

  try {
    output.log({
      title: 'Starting Nx MCP Server',
      bodyLines: [
        `Workspace: ${workspaceRoot}`,
        verbose ? 'Verbose logging: enabled' : '',
      ].filter(Boolean),
    });

    // Import the simple MCP server implementation
    const { SimpleNxMcpServer } = await import('../../mcp');

    // Create and start the MCP server
    const mcpServer = new SimpleNxMcpServer();
    const server = mcpServer.getMcpServer();

    // Connect to stdio for MCP protocol
    const transport = server.server.connectStdio();

    output.success({
      title: 'Nx MCP Server Started',
      bodyLines: [
        'The MCP server is now running and ready to serve Nx workspace information.',
        'Connected via stdio for Model Context Protocol communication.',
        '',
        'Available tools:',
        '- nx_workspace_path: Get workspace root path',
        '- nx_workspace: Get workspace configuration and project list',
        '- nx_project_details: Get detailed project configuration',
        '- nx_list_projects: List all projects with optional filtering',
        '- nx_show_projects: Show projects in readable format',
      ],
    });

    // Handle graceful shutdown
    process.on('SIGINT', () => {
      output.log({
        title: 'Shutting down Nx MCP Server',
        bodyLines: ['Goodbye!'],
      });
      process.exit(0);
    });

    process.on('SIGTERM', () => {
      output.log({
        title: 'Shutting down Nx MCP Server',
        bodyLines: ['Goodbye!'],
      });
      process.exit(0);
    });

    // Wait for the transport to close
    await transport;
  } catch (error) {
    output.error({
      title: 'Failed to start Nx MCP Server',
      bodyLines: [error.message],
    });
    process.exit(1);
  }
}

