import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { z } from 'zod';
import { readNxJson } from '../../config/nx-json';
import { workspaceRoot } from '../../utils/workspace-root';
import { findMatchingProjects } from '../../utils/find-matching-projects';
import { createProjectGraphAsync } from '../../project-graph/project-graph';
import { getMcpLogger, type Logger } from './mcp-logger';

export interface SimpleNxWorkspaceInfoProvider {
  getWorkspaceInfo: () => any;
  getProjectDetails: (projectName: string) => any;
  getProjects: () => Map<string, any>;
}

export class SimpleNxMcpServer {
  private server: McpServer;
  private logger: Logger;

  constructor(logger?: Logger) {
    this.server = new McpServer({
      name: 'Nx MCP Server',
      version: '1.0.0',
    });

    this.server.server.registerCapabilities({
      logging: {},
    });

    this.logger = logger ?? getMcpLogger(this.server);
    this.registerTools();
  }

  getMcpServer(): McpServer {
    return this.server;
  }

  private registerTools(): void {
    // Tool: Get workspace path
    this.server.tool(
      'nx_workspace_path',
      'Returns the path to the Nx workspace root',
      {},
      async () => {
        return {
          content: [
            {
              type: 'text',
              text: workspaceRoot,
            },
          ],
        };
      }
    );

    // Tool: Get workspace info
    this.server.tool(
      'nx_workspace',
      'Returns information about the Nx workspace including nx.json and project list',
      {},
      async () => {
        try {
          const nxJson = readNxJson();
          const projectGraph = await createProjectGraphAsync();

          const projectList = Object.entries(projectGraph.nodes).map(([name, node]) => ({
            name,
            root: node.data.root,
            projectType: node.type,
            targets: Object.keys(node.data.targets || {}),
            tags: node.data.tags || [],
          }));

          const workspaceInfo = {
            nxJson: {
              npmScope: nxJson.npmScope,
              affected: nxJson.affected,
              targetDefaults: nxJson.targetDefaults,
              namedInputs: nxJson.namedInputs,
              plugins: nxJson.plugins,
            },
            projects: projectList,
            workspaceRoot,
            projectCount: projectList.length,
          };

          return {
            content: [
              {
                type: 'text',
                text: JSON.stringify(workspaceInfo, null, 2),
              },
            ],
          };
        } catch (error) {
          return {
            isError: true,
            content: [
              {
                type: 'text',
                text: `Error reading workspace: ${error.message}`,
              },
            ],
          };
        }
      }
    );

    // Tool: Get project details
    this.server.tool(
      'nx_project_details',
      'Returns detailed configuration for a specific project',
      {
        projectName: z.string().describe('The name of the project'),
      },
      async ({ projectName }) => {
        try {
          const projectGraph = await createProjectGraphAsync();
          const project = projectGraph.nodes[projectName];

          if (!project) {
            // Try to find matching projects
            const projectNames = Object.keys(projectGraph.nodes);
            const matchingProjects = findMatchingProjects([projectName], projectNames);
            if (matchingProjects.length > 0) {
              const match = projectGraph.nodes[matchingProjects[0]];
              return {
                content: [
                  {
                    type: 'text',
                    text: JSON.stringify(match.data, null, 2),
                  },
                ],
              };
            }

            return {
              isError: true,
              content: [
                {
                  type: 'text',
                  text: `Project "${projectName}" not found`,
                },
              ],
            };
          }

          return {
            content: [
              {
                type: 'text',
                text: JSON.stringify(project.data, null, 2),
              },
            ],
          };
        } catch (error) {
          return {
            isError: true,
            content: [
              {
                type: 'text',
                text: `Error getting project details: ${error.message}`,
              },
            ],
          };
        }
      }
    );

    // Tool: List projects
    this.server.tool(
      'nx_list_projects',
      'Returns a list of all projects in the workspace',
      {
        filter: z.string().optional().describe('Optional filter pattern for project names'),
      },
      async ({ filter }) => {
        try {
          const projectGraph = await createProjectGraphAsync();
          let projectNames = Object.keys(projectGraph.nodes);

          if (filter) {
            const matchingProjects = findMatchingProjects([filter], projectNames);
            projectNames = matchingProjects;
          }

          const projectList = projectNames.map(name => {
            const node = projectGraph.nodes[name];
            return {
              name,
              root: node.data.root,
              projectType: node.type,
              targets: Object.keys(node.data.targets || {}),
            };
          });

          return {
            content: [
              {
                type: 'text',
                text: JSON.stringify(projectList, null, 2),
              },
            ],
          };
        } catch (error) {
          return {
            isError: true,
            content: [
              {
                type: 'text',
                text: `Error listing projects: ${error.message}`,
              },
            ],
          };
        }
      }
    );

    // Tool: Show project graph info
    this.server.tool(
      'nx_show_projects',
      'Show projects and their dependencies in a readable format',
      {
        projects: z.string().optional().describe('Comma-separated list of projects to show'),
      },
      async ({ projects: projectsParam }) => {
        try {
          const projectGraph = await createProjectGraphAsync();
          let projectsToShow = Object.keys(projectGraph.nodes);

          if (projectsParam) {
            projectsToShow = projectsParam.split(',').map(p => p.trim());
          }

          const result = projectsToShow.map(projectName => {
            const node = projectGraph.nodes[projectName];
            if (!node) {
              return `Project "${projectName}" not found`;
            }

            return `
Project: ${projectName}
  Root: ${node.data.root}
  Type: ${node.type || 'unknown'}
  Targets: ${Object.keys(node.data.targets || {}).join(', ') || 'none'}
  Tags: ${node.data.tags?.join(', ') || 'none'}
            `.trim();
          }).join('\n\n');

          return {
            content: [
              {
                type: 'text',
                text: result,
              },
            ],
          };
        } catch (error) {
          return {
            isError: true,
            content: [
              {
                type: 'text',
                text: `Error showing projects: ${error.message}`,
              },
            ],
          };
        }
      }
    );
  }
}

