import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import { z } from 'zod';
import { getMcpLogger, Logger } from './mcp-logger';
import { workspaceRoot } from '../../utils/workspace-root';
import { readNxJson } from '../../config/nx-json';
import { createProjectGraphAsync } from '../../project-graph/project-graph';
import { readProjectsConfigurationFromProjectGraph } from '../../project-graph/project-graph';

export class NxMcpServer {
  private server: McpServer;
  private logger: Logger;
  private workspacePath: string;

  constructor(workspacePath?: string) {
    this.workspacePath = workspacePath || workspaceRoot;
    this.server = new McpServer({
      name: 'Nx MCP',
      version: '0.0.1',
    });

    this.server.server.registerCapabilities({
      logging: {},
    });

    this.logger = getMcpLogger(this.server);
  }

  static async create(workspacePath?: string): Promise<NxMcpServer> {
    const server = new NxMcpServer(workspacePath);
    await server.registerTools();
    return server;
  }

  getMcpServer(): McpServer {
    return this.server;
  }

  private async registerTools(): Promise<void> {
    // Nx workspace path tool
    this.server.tool(
      'nx_workspace_path',
      'Returns the path to the Nx workspace root',
      {},
      async () => {
        return {
          content: [
            {
              type: 'text',
              text: this.workspacePath,
            },
          ],
        };
      }
    );

    // Nx workspace info tool
    this.server.tool(
      'nx_workspace',
      'Returns a readable representation of the nx project graph and the nx.json that configures nx',
      {},
      async () => {
        try {
          const nxJson = readNxJson(this.workspacePath);
          const projectGraph = await createProjectGraphAsync();
          const projectsConfiguration = readProjectsConfigurationFromProjectGraph(projectGraph);

          const content = [];

          if (nxJson) {
            content.push({
              type: 'text',
              text: `Nx Configuration:\n${JSON.stringify(nxJson, null, 2)}`,
            });
          }

          if (projectGraph && Object.keys(projectGraph.nodes).length > 0) {
            const projectList = Object.keys(projectGraph.nodes).map(name => {
              const project = projectGraph.nodes[name];
              return `- ${name}: ${project.data.root} (${project.type})`;
            }).join('\n');

            content.push({
              type: 'text',
              text: `Projects (${Object.keys(projectGraph.nodes).length}):\n${projectList}`,
            });
          }

          return { content };
        } catch (e) {
          return {
            content: [{ type: 'text', text: `Error: ${String(e)}` }],
          };
        }
      }
    );

    // Project details tool
    this.server.tool(
      'nx_project_details',
      'Returns the complete project configuration in JSON format for a given nx project',
      {
        projectName: z
          .string()
          .describe('The name of the project to get details for'),
      },
      async ({ projectName }) => {
        try {
          const projectGraph = await createProjectGraphAsync();
          const project = projectGraph.nodes[projectName];

          if (!project) {
            return {
              isError: true,
              content: [
                {
                  type: 'text',
                  text: `Project ${projectName} not found`,
                },
              ],
            };
          }

          return {
            content: [
              { type: 'text', text: JSON.stringify(project.data, null, 2) },
            ],
          };
        } catch (e) {
          return {
            content: [{ type: 'text', text: `Error: ${String(e)}` }],
          };
        }
      }
    );

    // Available plugins tool (simplified)
    this.server.tool(
      'nx_available_plugins',
      'Returns a list of available Nx plugins from the core team',
      {},
      async () => {
        const corePlugins = [
          '@nx/angular',
          '@nx/react',
          '@nx/next',
          '@nx/nest',
          '@nx/express',
          '@nx/node',
          '@nx/js',
          '@nx/web',
          '@nx/cypress',
          '@nx/jest',
          '@nx/storybook',
          '@nx/eslint',
          '@nx/webpack',
          '@nx/vite',
          '@nx/rspack',
          '@nx/esbuild',
        ];

        return {
          content: [
            {
              type: 'text',
              text: `Available Nx Core Plugins:\n${corePlugins.map(p => `- ${p}`).join('\n')}`,
            },
          ],
        };
      }
    );

    this.logger.log('Nx MCP Server tools registered successfully');
  }
}

