import { CommandModule } from 'yargs';
import { NxMcpServer } from '../../mcp';
import { workspaceRoot } from '../../utils/workspace-root';
import { logger } from '../../utils/logger';

export interface McpOptions {
  stdio?: boolean;
}

export const mcpCommand: CommandModule<{}, McpOptions> = {
  command: 'mcp',
  describe: 'Start the Nx MCP (Model Context Protocol) server',
  builder: (yargs) =>
    yargs.option('stdio', {
      type: 'boolean',
      default: true,
      description: 'Use stdio transport for MCP server',
    }),
  handler: async (args) => {
    try {
      logger.info('Starting Nx MCP Server...');

      const mcpServer = await NxMcpServer.create(workspaceRoot);
      const server = mcpServer.getMcpServer();

      if (args.stdio) {
        const transport = server.server.stdio();
        await server.connect(transport);
        logger.info('Nx MCP Server started on stdio');
      } else {
        logger.error('Only stdio transport is currently supported');
        process.exit(1);
      }
    } catch (error) {
      logger.error(`Failed to start Nx MCP Server: ${error.message}`);
      process.exit(1);
    }
  },
};

