async function getNxVersionFromRegistry(registry: string): Promise<string | undefined> {
  return new Promise((resolve) => {
    exec(
      `npm view nx@latest version --registry=${registry}`,
      {
        windowsHide: false,
      },
      (error, stdout) => {
        if (error) {
          return resolve(undefined);
        }
        return resolve(stdout.trim());
      }
    );
  });
}

async function waitForRegistryVersionDifference(
  defaultRegistry: string,
  localRegistry: string,
  maxAttempts: number = 60,
  pollIntervalMs: number = 1000
): Promise<string | undefined> {
  console.log('Waiting for Nx version to differ between registries...');

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    console.log(`Attempt ${attempt}/${maxAttempts}: Checking registry versions...`);

    const [defaultVersion, localVersion] = await Promise.all([
      getNxVersionFromRegistry(defaultRegistry),
      getNxVersionFromRegistry(localRegistry)
    ]);

    console.log(`Default registry (${defaultRegistry}): ${defaultVersion || 'undefined'}`);
    console.log(`Local registry (${localRegistry}): ${localVersion || 'undefined'}`);

    // If local version exists and differs from default, we're ready
    if (localVersion && defaultVersion && localVersion !== defaultVersion) {
      console.log(` Version difference detected! Using local version: ${localVersion}`);
