 28	  if (typeof globalThis.tuiOnProcessExit === 'function') {
 29	    globalThis.tuiOnProcessExit(...args);
 30	  }
 31	});
 32	
 33	function main() {
 34	  if (
 35	    process.argv[2] !== 'report' &&
 36	    process.argv[2] !== '--version' &&
 37	    process.argv[2] !== '--help' &&
 38	    process.argv[2] !== 'reset'
 39	  ) {
 40	    assertSupportedPlatform();
 41	  }
 42	
 43	  require('nx/src/utils/perf-logging');
 44	
 45	  const workspace = findWorkspaceRoot(process.cwd());
 46	
 47	  performance.mark('loading dotenv files:start');
 48	  if (workspace) {
 49	    loadRootEnvFiles(workspace.dir);
 50	  }
 51	  performance.mark('loading dotenv files:end');
 52	  performance.measure(
 53	    'loading dotenv files',
 54	    'loading dotenv files:start',
 55	    'loading dotenv files:end'
 56	  );
 57	
 58	  // new is a special case because there is no local workspace to load
 59	  if (
 60	    process.argv[2] === 'new' ||
 61	    process.argv[2] === '_migrate' ||
 62	    process.argv[2] === 'init' ||
 63	    (process.argv[2] === 'graph' && !workspace)
 64	  ) {
 65	    process.env.NX_DAEMON = 'false';
 66	    require('nx/src/command-line/nx-commands').commandsObject.argv;
 67	  } else {
 68	    if (!daemonClient.enabled() && workspace !== null) {
 69	      setupWorkspaceContext(workspace.dir);
 70	    }
