use std::process::Command;

const NX_CONSOLE_EXTENSION_ID: &str = "nrwl.angular-console";

#[napi::napi]
#[derive(Clone)]
pub struct VsCodeExtensionResult {
    pub success: bool,
    pub message: String,
    pub already_installed: Option<bool>,
}

pub fn find_vscode_command() -> Result<String, String> {
    // Try the standard 'code' command first
    if is_command_available("code") {
        return Ok("code".to_string());
    }

    // Platform-specific fallbacks
    #[cfg(target_os = "windows")]
    {
        // Check common Windows paths
        let paths = vec![
            r"C:\Program Files\Microsoft VS Code\bin\code.cmd",
            r"C:\Program Files (x86)\Microsoft VS Code\bin\code.cmd",
            format!(r"{}\.vscode\bin\code.cmd", std::env::var("LOCALAPPDATA").unwrap_or_default()),
        ];

        for path in paths {
            if std::path::Path::new(&path).exists() {
                return Ok(path);
            }
        }
    }

    #[cfg(target_os = "macos")]
    {
        // Check common macOS paths
        let paths = vec![
            "/usr/local/bin/code",
            "/Applications/Visual Studio Code.app/Contents/Resources/app/bin/code",
        ];

        for path in paths {
            if std::path::Path::new(&path).exists() {
                return Ok(path.to_string());
            }
        }
    }

    Err("VS Code command not found. Please ensure VS Code is installed and the 'code' command is available in PATH.".to_string())
}

fn is_command_available(command: &str) -> bool {
    Command::new(command)
        .arg("--version")
        .output()
        .map(|output| output.status.success())
        .unwrap_or(false)
}

fn is_extension_installed(code_command: &str) -> Result<bool, String> {
    let output = Command::new(code_command)
        .arg("--list-extensions")
        .output()
        .map_err(|e| format!("Failed to list VS Code extensions: {}", e))?;

    if !output.status.success() {
        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Failed to list VS Code extensions: {}", stderr));
    }

    let stdout = String::from_utf8_lossy(&output.stdout);
    Ok(stdout.lines().any(|line| line.trim() == NX_CONSOLE_EXTENSION_ID))
}

fn install_extension(code_command: &str) -> Result<(), String> {
    let output = Command::new(code_command)
        .arg("--install-extension")
        .arg(NX_CONSOLE_EXTENSION_ID)
        .arg("--force")
        .output()
        .map_err(|e| format!("Failed to install VS Code extension: {}", e))?;

    if !output.status.success() {
        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Failed to install VS Code extension: {}", stderr));
    }

    Ok(())
}

#[napi::napi]
pub fn ensure_nx_console_installed() -> VsCodeExtensionResult {
    // Find VS Code command
    let code_command = match find_vscode_command() {
        Ok(cmd) => cmd,
        Err(e) => {
            return VsCodeExtensionResult {
                success: false,
                message: e,
                already_installed: None,
            };
        }
    };

    // Check if already installed
    match is_extension_installed(&code_command) {
        Ok(true) => {
            return VsCodeExtensionResult {
                success: true,
                message: "Nx Console extension is already installed".to_string(),
                already_installed: Some(true),
            };
        }
        Ok(false) => {
            // Extension not installed, proceed with installation
        }
        Err(e) => {
            return VsCodeExtensionResult {
                success: false,
                message: format!("Error checking extension status: {}", e),
                already_installed: None,
            };
        }
    }

    // Install the extension
    match install_extension(&code_command) {
        Ok(()) => VsCodeExtensionResult {
            success: true,
            message: "Nx Console extension installed successfully".to_string(),
            already_installed: Some(false),
        },
        Err(e) => VsCodeExtensionResult {
            success: false,
            message: format!("Failed to install extension: {}", e),
            already_installed: Some(false),
        },
    }
}

