        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Failed to install VS Code extension: {}", stderr));
    }

    Ok(())
}

#[napi::napi]
pub fn ensure_nx_console_installed() -> VsCodeExtensionResult {
    // Find VS Code command
    let code_command = match find_vscode_command() {
        Ok(cmd) => cmd,
        Err(e) => {
            return VsCodeExtensionResult {
                success: false,
                message: e,
                already_installed: None,
            };
        }
    };

    // Check if already installed
    match is_extension_installed(&code_command) {
        Ok(true) => {
            return VsCodeExtensionResult {
                success: true,
                message: "Nx Console extension is already installed".to_string(),
                already_installed: Some(true),
            };
        }
        Ok(false) => {
            // Extension not installed, proceed with installation
        }
        Err(e) => {
            return VsCodeExtensionResult {
                success: false,
                message: format!("Error checking extension status: {}", e),
                already_installed: None,
            };
        }
    }

    // Install the extension
    match install_extension(&code_command) {
        Ok(()) => VsCodeExtensionResult {
            success: true,
            message: "Nx Console extension installed successfully".to_string(),
            already_installed: Some(false),
        },
        Err(e) => VsCodeExtensionResult {
            success: false,
            message: format!("Failed to install extension: {}", e),
            already_installed: Some(false),
        },
    }
}

