 92	        return;
 93	    }
 94	
 95	    // Use the sophisticated editor detection from nx_console
 96	    let current_editor = get_current_editor();
 97	    debug!("Detected editor: {:?}", current_editor);
 98	
 99	    let command = match current_editor {
100	        SupportedEditor::VSCode => {
101	            debug!("Installing Nx Console extension for VS Code");
102	            #[cfg(target_os = "windows")]
103	            {
104	                "code.cmd"
105	            }
106	            #[cfg(not(target_os = "windows"))]
107	            {
108	                "code"
109	            }
110	        }
111	        SupportedEditor::Windsurf => {
112	            debug!("Installing Nx Console extension for Windsurf");
113	            #[cfg(target_os = "windows")]
114	            {
115	                "windsurf.cmd"
116	            }
117	            #[cfg(not(target_os = "windows"))]
118	            {
119	                "windsurf"
120	            }
121	        }
122	        editor => {
123	            trace!(
124	                "Unknown editor ({editor:?}) detected, skipping Nx Console extension installation"
125	            );
126	            return;
127	        }
128	    };
129	
130	    // Try to install the extension
131	    if let Err(e) = install_nx_console(command) {
132	        debug!("Failed to install Nx Console extension: {}", e);
133	    }
