use serde::{Deserialize, Serialize};
use std::fs;
use std::path::{Path, PathBuf};
use tracing::debug;
use std::time::{SystemTime, UNIX_EPOCH};

const NX_CONSOLE_PREFERENCES_FILE_NAME: &str = "ide.json";

#[napi]
#[derive(Debug, Serialize, Deserialize)]
pub struct NxConsolePreferences {
    pub auto_install_console: Option<bool>,
    pub typescript_prompt_enabled: Option<bool>,
    pub typescript_prompt_last_shown: Option<String>, // ISO datetime string
    path: PathBuf,
    loaded: bool,
}

#[napi]
impl NxConsolePreferences {
    #[napi(constructor)]
    pub fn new(home_dir: String) -> Self {
        let home_dir = PathBuf::from(home_dir);
        let config_dir = get_user_config_dir(home_dir);
        Self {
            auto_install_console: None,
            typescript_prompt_enabled: None,
            typescript_prompt_last_shown: None,
            path: config_dir.join(NX_CONSOLE_PREFERENCES_FILE_NAME),
            loaded: false,
        }
    }

    #[napi]
    pub fn wants_auto_install(&mut self) -> bool {
        match self.load() {
            Ok(prefs) => match prefs.auto_install_console {
                Some(auto_install) => {
                    debug!("Auto-install preference loaded: {}", auto_install);
                    auto_install
                }
                None => {
                    debug!("Auto-install preference not set, defaulting to false.");
                    false
                }
            },
            _ => {
                debug!("Failed to load console preferences.");
                false
            }
        }
    }

    #[napi]
    pub fn set_auto_install_preference(&mut self, auto_install: bool) {
        self.auto_install_console = Some(auto_install);
        if let Err(err) = self.save() {
            debug!("Failed to save console preferences: {}", err);
        } else {
            debug!("Console preferences saved successfully.");
        }
    }

    #[napi]
    pub fn should_show_typescript_prompt(&mut self) -> bool {
        match self.load() {
            Ok(prefs) => {
                // If explicitly disabled, don't show
                if let Some(false) = prefs.typescript_prompt_enabled {
                    debug!("TypeScript prompt explicitly disabled");
                    return false;
                }

                // If never shown or enabled, show it
                if prefs.typescript_prompt_last_shown.is_none() {
                    debug!("TypeScript prompt never shown, should show");
                    return true;
                }

                // Check if enough time has passed since last shown (7 days)
                if let Some(last_shown) = &prefs.typescript_prompt_last_shown {
                    if let Ok(last_timestamp) = last_shown.parse::<u64>() {
                        let now = SystemTime::now()
                            .duration_since(UNIX_EPOCH)
                            .map(|d| d.as_secs())
                            .unwrap_or(0);
                        let seconds_since = now.saturating_sub(last_timestamp);
                        let days_since = seconds_since / (24 * 60 * 60);
                        let should_show = days_since >= 7;
                        debug!("TypeScript prompt last shown {} days ago, should show: {}", days_since, should_show);
                        return should_show;
                    }
                }

                debug!("Invalid last shown date, defaulting to show");
                true
            }
            _ => {
                debug!("Failed to load preferences for TypeScript prompt");
                true // Default to showing if we can't load preferences
            }
        }
    }

    #[napi]
    pub fn set_typescript_prompt_preference(&mut self, enabled: bool) {
        self.typescript_prompt_enabled = Some(enabled);
        self.typescript_prompt_last_shown = Some(chrono::Utc::now().to_rfc3339());
        if let Err(err) = self.save() {
            debug!("Failed to save TypeScript prompt preferences: {}", err);
        } else {
            debug!("TypeScript prompt preferences saved successfully.");
        }
    }

    #[napi]
    pub fn mark_typescript_prompt_shown(&mut self) {
        self.typescript_prompt_last_shown = Some(chrono::Utc::now().to_rfc3339());
        if let Err(err) = self.save() {
            debug!("Failed to save TypeScript prompt timestamp: {}", err);
        } else {
            debug!("TypeScript prompt timestamp updated successfully.");
        }
    }

    fn save(&self) -> anyhow::Result<()> {
        fs::create_dir_all(&self.path)?;

        let content = serde_json::to_string_pretty(self)?;

        fs::write(&self.path, content)?;
        Ok(())
    }

    fn load(&mut self) -> anyhow::Result<NxConsolePreferences> {
        let content = fs::read_to_string(&self.path)?;
        let prefs = serde_json::from_str::<NxConsolePreferences>(&content)?;

        debug!("Loaded console preferences: {:?}", prefs);
        self.loaded = true;
        Ok(prefs)
    }
}

