use crate::native::ide::detection::{get_current_editor, SupportedEditor};
use crate::native::ide::preferences::NxConsolePreferences;
use crate::native::logger::enable_logger;
use tracing::{debug, info};

/// Checks if a TypeScript prompt should be shown to the user
/// This considers user preferences, editor detection, and timing
#[napi]
pub fn should_show_typescript_prompt(home_dir: String) -> bool {
    enable_logger();

    // Check if we're in a supported editor
    let current_editor = get_current_editor();
    debug!("Detected editor for TypeScript prompt: {:?}", current_editor);

    match current_editor {
        SupportedEditor::VSCode | SupportedEditor::Windsurf => {
            // Check user preferences for TypeScript prompts
            let mut preferences = NxConsolePreferences::new(home_dir);
            let should_show = preferences.should_show_typescript_prompt();
            debug!("TypeScript prompt decision: {}", should_show);
            should_show
        }
        _ => {
            debug!("TypeScript prompt skipped - unsupported editor: {:?}", current_editor);
            false
        }
    }
}

/// Marks that the TypeScript prompt was shown to the user
/// This updates the timestamp to prevent showing too frequently
#[napi]
pub fn mark_typescript_prompt_shown(home_dir: String) {
    enable_logger();

    let mut preferences = NxConsolePreferences::new(home_dir);
    preferences.mark_typescript_prompt_shown();
    info!("TypeScript prompt shown timestamp updated");
}

/// Sets the user's preference for TypeScript prompts
/// When enabled=false, the prompt will be permanently disabled
/// When enabled=true, the prompt will follow normal timing rules
#[napi]
pub fn set_typescript_prompt_preference(home_dir: String, enabled: bool) {
    enable_logger();

    let mut preferences = NxConsolePreferences::new(home_dir);
    preferences.set_typescript_prompt_preference(enabled);

    if enabled {
        info!("TypeScript prompts enabled by user");
    } else {
        info!("TypeScript prompts permanently disabled by user");
    }
}

/// Check if TypeScript-related files exist in the current workspace
/// This can be used to determine if TypeScript prompts are relevant
#[napi]
pub fn workspace_uses_typescript(workspace_root: String) -> bool {
    enable_logger();

    use std::path::Path;

    let root_path = Path::new(&workspace_root);

    // Check for common TypeScript indicators
    let typescript_indicators = [
        "tsconfig.json",
        "tsconfig.base.json", 
        "nx.json", // Nx workspaces commonly use TypeScript
        "packages.json", // might contain TypeScript dependencies
    ];

    for indicator in &typescript_indicators {
        let indicator_path = root_path.join(indicator);
        if indicator_path.exists() {
            debug!("Found TypeScript indicator: {}", indicator);
            return true;
        }
    }

    // Check for .ts files in common locations
    let ts_dirs = ["src", "libs", "apps", "packages"];
    for dir in &ts_dirs {
        let dir_path = root_path.join(dir);
        if dir_path.exists() && has_typescript_files(&dir_path) {
            debug!("Found TypeScript files in {}", dir);
            return true;
        }
    }

    debug!("No TypeScript indicators found in workspace");
    false
}

/// Helper function to recursively check for TypeScript files
fn has_typescript_files(dir: &Path) -> bool {
    use std::fs;

    if let Ok(entries) = fs::read_dir(dir) {
        for entry in entries.flatten() {
            let path = entry.path();
            if path.is_file() {
                if let Some(extension) = path.extension() {
                    if extension == "ts" || extension == "tsx" {
                        return true;
                    }
                }
            } else if path.is_dir() {
                // Recursively check subdirectories (but limit depth to avoid performance issues)
                if has_typescript_files(&path) {
                    return true;
                }
            }
        }
    }

    false
}

/// Get a formatted prompt message for TypeScript setup
/// This can be customized based on the detected workspace setup
#[napi]
pub fn get_typescript_prompt_message(workspace_root: String) -> String {
    let uses_ts = workspace_uses_typescript(workspace_root);

    if uses_ts {
        "Nx can help optimize your TypeScript setup with better compilation and type checking. Would you like to see TypeScript configuration recommendations?".to_string()
    } else {
        "Nx works great with TypeScript! TypeScript provides better tooling, error checking, and developer experience. Would you like to learn about setting up TypeScript in your Nx workspace?".to_string()
    }
}

