  5	
  6	/// Checks if a TypeScript prompt should be shown to the user
  7	/// This considers user preferences, editor detection, and timing
  8	#[napi]
  9	pub fn should_show_typescript_prompt(home_dir: String) -> bool {
 10	    enable_logger();
 11	    
 12	    // Check if we're in a supported editor
 13	    let current_editor = get_current_editor();
 14	    debug!("Detected editor for TypeScript prompt: {:?}", current_editor);
 15	    
 16	    match current_editor {
 17	        SupportedEditor::VSCode | SupportedEditor::Windsurf => {
 18	            // Check user preferences for TypeScript prompts
 19	            let mut preferences = NxConsolePreferences::new(home_dir);
 20	            let should_show = preferences.should_show_typescript_prompt();
 21	            debug!("TypeScript prompt decision: {}", should_show);
 22	            should_show
 23	        }
 24	        _ => {
 25	            debug!("TypeScript prompt skipped - unsupported editor: {:?}", current_editor);
 26	            false
 27	        }
 28	    }
 29	}
 30	
 31	/// Marks that the TypeScript prompt was shown to the user
 32	/// This updates the timestamp to prevent showing too frequently
 33	#[napi]
 34	pub fn mark_typescript_prompt_shown(home_dir: String) {
 35	    enable_logger();
 36	    
 37	    let mut preferences = NxConsolePreferences::new(home_dir);
 38	    preferences.mark_typescript_prompt_shown();
 39	    info!("TypeScript prompt shown timestamp updated");
 40	}
 41	
 42	/// Sets the user's preference for TypeScript prompts
 43	/// When enabled=false, the prompt will be permanently disabled
 44	/// When enabled=true, the prompt will follow normal timing rules
 45	#[napi]
 46	pub fn set_typescript_prompt_preference(home_dir: String, enabled: bool) {
 47	    enable_logger();
 48	    
 49	    let mut preferences = NxConsolePreferences::new(home_dir);
 50	    preferences.set_typescript_prompt_preference(enabled);
 51	    
 52	    if enabled {
 53	        info!("TypeScript prompts enabled by user");
 54	    } else {
 55	        info!("TypeScript prompts permanently disabled by user");
 56	    }
 57	}
 58	
 59	/// Check if TypeScript-related files exist in the current workspace
 60	/// This can be used to determine if TypeScript prompts are relevant
 61	#[napi]
 62	pub fn workspace_uses_typescript(workspace_root: String) -> bool {
 63	    enable_logger();
 64	    
 65	    use std::path::Path;
 66	    
 67	    let root_path = Path::new(&workspace_root);
 68	    
 69	    // Check for common TypeScript indicators
 70	    let typescript_indicators = [
 71	        "tsconfig.json",
 72	        "tsconfig.base.json", 
 73	        "nx.json", // Nx workspaces commonly use TypeScript
 74	        "packages.json", // might contain TypeScript dependencies
 75	    ];
 76	    
 77	    for indicator in &typescript_indicators {
 78	        let indicator_path = root_path.join(indicator);
 79	        if indicator_path.exists() {
 80	            debug!("Found TypeScript indicator: {}", indicator);
 81	            return true;
 82	        }
 83	    }
 84	    
 85	    // Check for .ts files in common locations
 86	    let ts_dirs = ["src", "libs", "apps", "packages"];
 87	    for dir in &ts_dirs {
 88	        let dir_path = root_path.join(dir);
 89	        if dir_path.exists() && has_typescript_files(&dir_path) {
 90	            debug!("Found TypeScript files in {}", dir);
 91	            return true;
 92	        }
 93	    }
 94	    
 95	    debug!("No TypeScript indicators found in workspace");
 96	    false
 97	}
 98	
 99	/// Helper function to recursively check for TypeScript files
100	fn has_typescript_files(dir: &Path) -> bool {
101	    use std::fs;
102	    
103	    if let Ok(entries) = fs::read_dir(dir) {
104	        for entry in entries.flatten() {
105	            let path = entry.path();
106	            if path.is_file() {
107	                if let Some(extension) = path.extension() {
108	                    if extension == "ts" || extension == "tsx" {
109	                        return true;
110	                    }
111	                }
112	            } else if path.is_dir() {
113	                // Recursively check subdirectories (but limit depth to avoid performance issues)
114	                if has_typescript_files(&path) {
115	                    return true;
116	                }
117	            }
118	        }
119	    }
120	    
121	    false
122	}
123	
124	/// Get a formatted prompt message for TypeScript setup
125	/// This can be customized based on the detected workspace setup
126	#[napi]
127	pub fn get_typescript_prompt_message(workspace_root: String) -> String {
128	    let uses_ts = workspace_uses_typescript(workspace_root);
129	    
130	    if uses_ts {
131	        "Nx can help optimize your TypeScript setup with better compilation and type checking. Would you like to see TypeScript configuration recommendations?".to_string()
132	    } else {
133	        "Nx works great with TypeScript! TypeScript provides better tooling, error checking, and developer experience. Would you like to learn about setting up TypeScript in your Nx workspace?".to_string()
134	    }
