/**
 * Example TypeScript Usage of Nx Console TypeScript Prompt Functions
 * 
 * This demonstrates how to use the new TypeScript prompt functionality
 * that integrates with the NxConsolePreferences Rust struct.
 */

import { 
  shouldShowTypescriptPrompt,
  markTypescriptPromptShown, 
  setTypescriptPromptPreference,
  workspaceUsesTypescript,
  getTypescriptPromptMessage,
  NxConsolePreferences
} from '../packages/nx/src/native';
import { homedir } from 'os';
import { join } from 'path';

// Example: Basic prompt checking workflow
async function checkAndShowTypescriptPrompt() {
  const homeDir = homedir();
  const workspaceRoot = process.cwd();

  // Check if we should show the TypeScript prompt
  const shouldShow = shouldShowTypescriptPrompt(homeDir);

  if (shouldShow) {
    // Check if workspace uses TypeScript to customize message
    const usesTypeScript = workspaceUsesTypescript(workspaceRoot);
    const promptMessage = getTypescriptPromptMessage(workspaceRoot);

    console.log('\n Nx TypeScript Setup');
    console.log(promptMessage);
    console.log('\nOptions:');
    console.log('1. Show me TypeScript recommendations');
    console.log('2. Not now (ask again in 7 days)');
    console.log('3. Never ask again');

    // Simulate user interaction (in real usage, this would be actual user input)
    const userChoice = await getUserChoice();

    switch (userChoice) {
      case '1':
        // User wants to see recommendations
        await showTypescriptRecommendations(workspaceRoot, usesTypeScript);
        markTypescriptPromptShown(homeDir);
        break;

      case '2':
        // User chose "not now" - just mark as shown, will ask again in 7 days
        markTypescriptPromptShown(homeDir);
        console.log(' We\'ll remind you about TypeScript setup in 7 days');
        break;

      case '3':
        // User never wants to see this prompt
        setTypescriptPromptPreference(homeDir, false);
        console.log(' TypeScript prompts have been disabled');
        break;
    }
  } else {
    console.log('TypeScript prompt not needed right now');
  }
}

// Example: Using NxConsolePreferences class directly
function managePreferencesDirectly() {
  const homeDir = homedir();
  const preferences = new NxConsolePreferences(homeDir);

  // Check current preferences
  const shouldShowPrompt = preferences.shouldShowTypescriptPrompt();
  console.log('Should show TypeScript prompt:', shouldShowPrompt);

  // Enable TypeScript prompts
  preferences.setTypescriptPromptPreference(true);
  console.log('TypeScript prompts enabled');

  // Mark prompt as shown (updates timestamp)
  preferences.markTypescriptPromptShown();
  console.log('Prompt timestamp updated');
}

// Example: Workspace analysis
function analyzeWorkspaceForTypescript() {
  const workspaceRoot = process.cwd();
  const usesTS = workspaceUsesTypescript(workspaceRoot);

  if (usesTS) {
    console.log(' This workspace already uses TypeScript');
    console.log('Prompt message:', getTypescriptPromptMessage(workspaceRoot));
  } else {
    console.log(' This workspace could benefit from TypeScript');
    console.log('Prompt message:', getTypescriptPromptMessage(workspaceRoot));
  }
}

// Example: CI/Scripts usage - disable prompts programmatically
function disablePromptsForCI() {
  const homeDir = homedir();

  // In CI environments, you might want to disable prompts entirely
  if (process.env.CI === 'true') {
    setTypescriptPromptPreference(homeDir, false);
    console.log('TypeScript prompts disabled for CI environment');
  }
}

// Mock functions for example purposes
async function getUserChoice(): Promise<string> {
  // In real implementation, this would use readline or similar
  return '2'; // Simulate "not now" choice
}

async function showTypescriptRecommendations(workspaceRoot: string, usesTypeScript: boolean) {
  if (usesTypeScript) {
    console.log('\n TypeScript Optimization Recommendations:');
    console.log(' Enable strict mode in tsconfig.json');
    console.log(' Use Nx cache for TypeScript compilation');
    console.log(' Set up project references for better performance');
    console.log(' Consider using @nx/esbuild for faster builds');
  } else {
    console.log('\n Getting Started with TypeScript in Nx:');
    console.log(' Run: nx g @nx/js:convert-to-typescript');
    console.log(' Add TypeScript to your dependencies');
    console.log(' Configure tsconfig.json for your workspace');
    console.log(' Migrate existing JavaScript files gradually');
  }
}

// Example usage patterns
if (require.main === module) {
  console.log('=== Nx Console TypeScript Prompt Examples ===\n');

  console.log('1. Checking and showing TypeScript prompt:');
  checkAndShowTypescriptPrompt().catch(console.error);

  console.log('\n2. Managing preferences directly:');
  managePreferencesDirectly();

  console.log('\n3. Analyzing workspace:');
  analyzeWorkspaceForTypescript();

  console.log('\n4. CI environment handling:');
  disablePromptsForCI();
}

export {
  checkAndShowTypescriptPrompt,
  managePreferencesDirectly,
  analyzeWorkspaceForTypescript,
  disablePromptsForCI
};

