  1	import { performance } from 'perf_hooks';
  2	
  3	import { commandsObject } from '../src/command-line/nx-commands';
  4	import { WorkspaceTypeAndRoot } from '../src/utils/find-workspace-root';
  5	import { stripIndents } from '../src/utils/strip-indents';
  6	import { maintainNxConsoleInstallation } from '../src/utils/nx-console-prompt';
  7	
  8	/**
  9	 * Nx is being run inside a workspace.
 10	 *
 11	 * @param workspace Relevant local workspace properties
 12	 */
 13	
 14	export function initLocal(workspace: WorkspaceTypeAndRoot) {
 15	  process.env.NX_CLI_SET = 'true';
 16	
 17	  try {
 18	    performance.mark('init-local');
 19	
 20	    if (workspace.type !== 'nx' && shouldDelegateToAngularCLI()) {
 21	      console.warn(
 22	        stripIndents`Using Nx to run Angular CLI commands is deprecated and will be removed in a future version.
 23	        To run Angular CLI commands, use \`ng\`.`
 24	      );
 25	      handleAngularCLIFallbacks(workspace);
 26	      return;
 27	    }
 28	
 29	    // Ensure NxConsole is installed if the user has it configured.
 30	    maintainNxConsoleInstallation();
 31	
 32	    const command = process.argv[2];
 33	    if (command === 'run' || command === 'g' || command === 'generate') {
 34	      commandsObject.parse(process.argv.slice(2));
 35	    } else if (isKnownCommand(command)) {
 36	      const newArgs = rewriteTargetsAndProjects(process.argv);
 37	      const help = newArgs.indexOf('--help');
 38	      const split = newArgs.indexOf('--');
 39	      if (help > -1 && (split === -1 || split > help)) {
 40	        commandsObject.showHelp();
 41	      } else {
 42	        commandsObject.parse(newArgs);
 43	      }
 44	    } else {
 45	      commandsObject.parse(process.argv.slice(2));
 46	    }
 47	  } catch (e) {
 48	    console.error(e.message);
 49	    process.exit(1);
 50	  }
 51	}
 52	
 53	export function rewriteTargetsAndProjects(args: string[]) {
 54	  const newArgs = [args[2]];
 55	  let i = 3;
 56	  while (i < args.length) {
 57	    if (args[i] === '--') {
 58	      return [...newArgs, ...args.slice(i)];
 59	    } else if (
 60	      args[i] === '-p' ||
 61	      args[i] === '--projects' ||
 62	      args[i] === '--exclude' ||
 63	      args[i] === '--files' ||
 64	      args[i] === '-t' ||
 65	      args[i] === '--target' ||
 66	      args[i] === '--targets'
 67	    ) {
 68	      newArgs.push(args[i]);
 69	      i++;
 70	      const items = [];
 71	      while (i < args.length && !args[i].startsWith('-')) {
 72	        items.push(args[i]);
 73	        i++;
 74	      }
 75	      newArgs.push(items.join(','));
 76	    } else {
 77	      newArgs.push(args[i]);
 78	      ++i;
 79	    }
 80	  }
 81	  return newArgs;
 82	}
 83	
 84	function isKnownCommand(command: string) {
 85	  const commands = [
 86	    ...Object.keys(
 87	      (commandsObject as any)
 88	        .getInternalMethods()
 89	        .getCommandInstance()
 90	        .getCommandHandlers()
 91	    ),
 92	    'g',
 93	    'dep-graph',
 94	    'affected:dep-graph',
 95	    'format',
 96	    'workspace-schematic',
 97	    'connect-to-nx-cloud',
 98	    'clear-cache',
 99	    'help',
100	  ];
101	  return !command || command.startsWith('-') || commands.indexOf(command) > -1;
102	}
103	
104	function shouldDelegateToAngularCLI() {
105	  const command = process.argv[2];
106	  const commands = [
107	    'analytics',
108	    'cache',
109	    'completion',
110	    'config',
111	    'doc',
112	    'update',
113	  ];
114	  return commands.indexOf(command) > -1;
115	}
116	
117	function handleAngularCLIFallbacks(workspace: WorkspaceTypeAndRoot) {
118	  if (process.argv[2] === 'update' && process.env.FORCE_NG_UPDATE != 'true') {
119	    console.log(
120	      `Nx provides a much improved version of "ng update". It runs the same migrations, but allows you to:`
121	    );
122	    console.log(`- rerun the same migration multiple times`);
123	    console.log(`- reorder migrations, skip migrations`);
124	    console.log(`- fix migrations that "almost work"`);
125	    console.log(`- commit a partially migrated state`);
126	    console.log(
127	      `- change versions of packages to match organizational requirements`
128	    );
129	    console.log(
130	      `And, in general, it is lot more reliable for non-trivial workspaces. Read more at: https://nx.dev/getting-started/nx-and-angular#ng-update-and-nx-migrate`
131	    );
132	    console.log(
133	      `Run "nx migrate latest" to update to the latest version of Nx.`
134	    );
135	    console.log(
