input.command:
gh issue list --repo nrwl/nx --state open --assignee "*" --limit 1000 --json assignees,labels,number | jq -r '.[] | 
{
  number: .number,
  assignees: .assignees[].login,
  high_priority: (if .labels | map(.name) | contains(["priority: high"]) then "yes" else "no" end)
} | "\(.assignees)\t\(.high_priority)"' | sort | uniq -c | awk '{if ($3 == "yes") print $2, $1; else other[$2] += $1} END {for (a in other) if (!(a in printed)) print a, 0}' | sort -k2 -nr
