  1→import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
  2→import {
  3→  createGeneratorUiResponseMessage,
  4→  getDocsContext,
  5→  getDocsPrompt,
  6→  getGeneratorNamesAndDescriptions,
  7→  getGeneratorSchema,
  8→  getGeneratorsPrompt,
  9→  getNxJsonPrompt,
 10→  getPluginsInformation,
 11→  getProjectGraphErrorsPrompt,
 12→  getProjectGraphPrompt,
 13→  getProjectGraphVisualizationMessage,
 14→  getTaskGraphVisualizationMessage,
 15→  NX_WORKSPACE_PATH,
 16→} from '@nx-console/shared-llm-context';
 17→import {
 18→  checkIsNxWorkspace,
 19→  findMatchingProject,
 20→} from '@nx-console/shared-npm';
 21→import { NxConsoleTelemetryLogger } from '@nx-console/shared-telemetry';
 22→import { Logger } from '@nx-console/shared-utils';
 23→import { z } from 'zod';
 24→import { getMcpLogger } from './mcp-logger';
 25→
 26→import { CallToolResult } from '@modelcontextprotocol/sdk/types.js';
 27→import { NxGeneratorsRequestOptions } from '@nx-console/language-server-types';
 28→import { NxVersion } from '@nx-console/nx-version';
 29→import { GeneratorCollectionInfo } from '@nx-console/shared-schema';
 30→import { NxWorkspace } from '@nx-console/shared-types';
 31→import { readFile } from 'fs/promises';
 32→import path from 'path';
 33→import { registerNxCloudTools } from './tools/nx-cloud';
 34→import {
 35→  NX_AVAILABLE_PLUGINS,
 36→  NX_DOCS,
 37→  NX_GENERATOR_SCHEMA,
 38→  NX_GENERATORS,
 39→  NX_PROJECT_DETAILS,
 40→  NX_RUN_GENERATOR,
 41→  NX_VISUALIZE_GRAPH,
 42→  NX_WORKSPACE,
 43→} from '@nx-console/shared-llm-context';
 44→import { registerNxTaskTools } from './tools/nx-tasks';
 45→
 46→export interface NxWorkspaceInfoProvider {
 47→  nxWorkspace: (
 48→    workspacePath: string,
 49→    logger: Logger,
 50→    reset?: boolean,
 51→  ) => Promise<NxWorkspace | undefined>;
 52→  getGenerators: (
 53→    workspacePath: string,
 54→    options?: NxGeneratorsRequestOptions,
 55→    logger?: Logger,
 56→  ) => Promise<GeneratorCollectionInfo[] | undefined>;
 57→  getGitDiffs: (
 58→    workspacePath: string,
 59→    baseSha?: string,
 60→    headSha?: string,
 61→  ) => Promise<{ path: string; diffContent: string }[] | null>;
 62→  isNxCloudEnabled: () => Promise<boolean>;
 63→}
 64→
 65→export interface NxIdeProvider {
 66→  ideName: 'vscode' | 'cursor' | 'windsurf';
 67→  focusProject: (projectName: string) => void;
 68→  focusTask: (projectName: string, taskName: string) => void;
 69→  showFullProjectGraph: () => void;
 70→  openGenerateUi: (
 71→    generatorName: string,
 72→    options: Record<string, unknown>,
 73→    cwd?: string,
 74→  ) => Promise<string>;
 75→}
 76→
 77→export class NxMcpServerWrapper {
 78→  private server: McpServer;
 79→  private logger: Logger;
 80→  private _nxWorkspacePath?: string;
 81→
 82→  constructor(
 83→    initialWorkspacePath: string | undefined,
 84→    private nxWorkspaceInfoProvider: NxWorkspaceInfoProvider,
 85→    private ideProvider?: NxIdeProvider,
 86→    private telemetry?: NxConsoleTelemetryLogger,
 87→    logger?: Logger,
 88→  ) {
 89→    this._nxWorkspacePath = initialWorkspacePath;
 90→    this.server = new McpServer({
 91→      name: 'Nx MCP',
 92→      version: '0.0.1',
 93→    });
 94→
 95→    this.server.server.registerCapabilities({
 96→      logging: {},
 97→    });
 98→
 99→    this.logger = logger ?? getMcpLogger(this.server);
100→  }
101→
102→  static async create(
103→    initialWorkspacePath: string | undefined,
104→    nxWorkspaceInfoProvider: NxWorkspaceInfoProvider,
105→    ideProvider?: NxIdeProvider,
106→    telemetry?: NxConsoleTelemetryLogger,
107→    logger?: Logger,
108→  ): Promise<NxMcpServerWrapper> {
109→    const server = new NxMcpServerWrapper(
110→      initialWorkspacePath,
111→      nxWorkspaceInfoProvider,
112→      ideProvider,
113→      telemetry,
114→      logger,
115→    );
116→    await server.registerTools();
117→    return server;
118→  }
119→
120→  async setNxWorkspacePath(path: string) {
121→    this.logger.log(`Setting nx workspace path to ${path}`);
122→    if (!this._nxWorkspacePath && !!path) {
123→      await this.registerWorkspaceTools();
124→    }
125→    this._nxWorkspacePath = path;
126→  }
127→
128→  getMcpServer(): McpServer {
129→    return this.server;
130→  }
131→
132→  private async registerTools(): Promise<void> {
133→    this.server.tool(
134→      NX_DOCS,
135→      'Returns a list of documentation sections that could be relevant to the user query. IMPORTANT: ALWAYS USE THIS IF YOU ARE ANSWERING QUESTIONS ABOUT NX. NEVER ASSUME KNOWLEDGE ABOUT NX BECAUSE IT WILL PROBABLY BE OUTDATED. Use it to learn about nx, its configuration and options instead of assuming knowledge about it.',
136→      {
137→        userQuery: z
138→          .string()
139→          .describe(
140→            'The user query to get docs for. You can pass the original user query verbatim or summarize it.',
141→          ),
142→      },
143→      {
144→        destructiveHint: false,
145→        readOnlyHint: true,
146→        openWorldHint: true,
147→      },
148→      async ({ userQuery }: { userQuery: string }) => {
149→        this.telemetry?.logUsage('ai.tool-call', {
150→          tool: NX_DOCS,
151→        });
152→        const docsPages = await getDocsContext(userQuery);
153→        return {
154→          content: [{ type: 'text', text: getDocsPrompt(docsPages) }],
155→        };
156→      },
157→    );
158→
159→    this.server.tool(
160→      NX_AVAILABLE_PLUGINS,
161→      'Returns a list of available Nx plugins from the core team as well as local workspace Nx plugins.',
162→      {
163→        destructiveHint: false,
164→        readOnlyHint: true,
165→        openWorldHint: true,
166→      },
167→      async () => {
168→        this.telemetry?.logUsage('ai.tool-call', {
169→          tool: NX_AVAILABLE_PLUGINS,
170→        });
171→
172→        let nxVersion: NxVersion | undefined = undefined;
173→        let nxWorkspace: NxWorkspace | undefined = undefined;
174→        const workspacePath: string | undefined = this._nxWorkspacePath;
175→
176→        if (this._nxWorkspacePath) {
177→          nxWorkspace = await this.nxWorkspaceInfoProvider.nxWorkspace(
178→            this._nxWorkspacePath,
179→            this.logger,
180→          );
181→          nxVersion = nxWorkspace?.nxVersion;
182→        }
183→
184→        const pluginsInfo = await getPluginsInformation(
185→          nxVersion,
186→          workspacePath,
187→          nxWorkspace,
188→          this.logger,
189→        );
190→
191→        return {
192→          content: [
193→            {
194→              type: 'text',
195→              text: pluginsInfo.formattedText,
196→            },
197→          ],
198→        };
199→      },
200→    );
201→
202→    if (this._nxWorkspacePath) {
203→      await this.registerWorkspaceTools();
204→    }
205→
206→    if (this.ideProvider) {
207→      this.registerIdeTools();
208→    }
209→  }
210→
211→  private async registerWorkspaceTools(): Promise<void> {
212→    this.server.tool(
213→      NX_WORKSPACE_PATH,
214→      'Returns the path to the Nx workspace root',
215→      {
216→        readOnlyHint: true,
217→      },
218→      async () => {
219→        this.telemetry?.logUsage('ai.tool-call', {
220→          tool: NX_WORKSPACE_PATH,
221→        });
222→
223→        return {
224→          content: [
225→            {
226→              type: 'text',
227→              text: this._nxWorkspacePath ?? 'No workspace path set',
228→            },
229→          ],
230→        };
231→      },
232→    );
233→
234→    this.server.tool(
235→      NX_WORKSPACE,
236→      'Returns a readable representation of the nx project graph and the nx.json that configures nx. If there are project graph errors, it also returns them. Use it to answer questions about the nx workspace and architecture',
237→      {
238→        destructiveHint: false,
239→        readOnlyHint: true,
240→        openWorldHint: false,
241→      },
242→      async () => {
243→        this.telemetry?.logUsage('ai.tool-call', {
244→          tool: NX_WORKSPACE,
245→        });
246→        try {
247→          if (!this._nxWorkspacePath) {
248→            return {
249→              isError: true,
250→              content: [
251→                { type: 'text', text: 'Error: Workspace path not set' },
252→              ],
253→            };
254→          }
255→          if (!(await checkIsNxWorkspace(this._nxWorkspacePath))) {
256→            return {
257→              isError: true,
258→              content: [
259→                {
260→                  type: 'text',
261→                  text: 'Error: The provided root is not a valid nx workspace.',
262→                },
263→              ],
264→            };
265→          }
266→
267→          const workspace = await this.nxWorkspaceInfoProvider.nxWorkspace(
268→            this._nxWorkspacePath,
269→            this.logger,
270→          );
271→          if (!workspace) {
272→            return {
273→              isError: true,
274→              content: [{ type: 'text', text: 'Error: Workspace not found' }],
275→            };
276→          }
277→          const content: CallToolResult['content'] = [];
278→          if (workspace.nxJson) {
279→            content.push({
280→              type: 'text',
281→              text: getNxJsonPrompt(workspace.nxJson),
282→            });
283→          }
284→          const hasProjects =
285→            workspace.projectGraph &&
286→            Object.keys(workspace.projectGraph.nodes).length > 0;
287→
288→          if (hasProjects) {
289→            content.push({
290→              type: 'text',
291→              text: getProjectGraphPrompt(workspace.projectGraph),
292→            });
293→          }
294→          if (workspace.errors) {
295→            content.push({
296→              type: 'text',
297→              text: getProjectGraphErrorsPrompt(
298→                workspace.errors,
299→                !!workspace.isPartial,
300→              ),
301→            });
302→          }
303→
304→          return {
305→            content,
306→          };
307→        } catch (e) {
308→          return {
309→            content: [{ type: 'text', text: String(e) }],
310→          };
311→        }
312→      },
313→    );
314→
315→    this.server.tool(
316→      NX_PROJECT_DETAILS,
317→      'Returns the complete project configuration in JSON format for a given nx project.',
318→      {
319→        projectName: z
320→          .string()
321→          .describe('The name of the project to get details for'),
322→      },
323→      {
324→        destructiveHint: false,
325→        readOnlyHint: true,
326→        openWorldHint: false,
327→      },
328→      async ({ projectName }) => {
329→        this.telemetry?.logUsage('ai.tool-call', {
330→          tool: NX_PROJECT_DETAILS,
331→        });
332→        if (!this._nxWorkspacePath) {
333→          return {
334→            isError: true,
335→            content: [{ type: 'text', text: 'Error: Workspace path not set' }],
336→          };
337→        }
338→        const workspace = await this.nxWorkspaceInfoProvider.nxWorkspace(
339→          this._nxWorkspacePath,
340→          this.logger,
341→        );
342→        if (!workspace) {
343→          return {
344→            isError: true,
345→            content: [{ type: 'text', text: 'Error: Workspace not found' }],
346→          };
347→        }
348→        const project = await findMatchingProject(
349→          projectName,
350→          workspace.projectGraph.nodes,
351→          this._nxWorkspacePath,
352→        );
353→
354→        if (!project) {
355→          return {
356→            isError: true,
357→            content: [
358→              {
359→                type: 'text',
360→                text: `Project ${projectName} not found`,
361→              },
362→            ],
363→          };
364→        }
365→
366→        return {
367→          content: [
368→            { type: 'text', text: JSON.stringify(project.data, null, 2) },
369→          ],
370→        };
371→      },
372→    );
373→
374→    this.server.tool(
375→      NX_GENERATORS,
376→      'Returns a list of generators that could be relevant to the user query.',
377→      {
378→        destructiveHint: false,
379→        readOnlyHint: true,
380→        openWorldHint: false,
381→      },
382→      async () => {
383→        this.telemetry?.logUsage('ai.tool-call', {
384→          tool: NX_GENERATORS,
385→        });
386→        if (!this._nxWorkspacePath) {
387→          return {
388→            isError: true,
389→            content: [{ type: 'text', text: 'Error: Workspace path not set' }],
390→          };
391→        }
392→        const generators = await this.nxWorkspaceInfoProvider.getGenerators(
393→          this._nxWorkspacePath,
394→          undefined,
395→          this.logger,
396→        );
397→        if (!generators) {
398→          return {
399→            content: [{ type: 'text', text: 'No generators found' }],
400→          };
401→        }
402→        if (generators.length === 0) {
403→          return {
404→            content: [{ type: 'text', text: 'No generators found' }],
405→          };
406→        }
407→
408→        const generatorNamesAndDescriptions =
409→          await getGeneratorNamesAndDescriptions(generators);
410→        const prompt = await getGeneratorsPrompt(generatorNamesAndDescriptions);
411→        return {
412→          content: [{ type: 'text', text: prompt }],
413→        };
414→      },
415→    );
416→
417→    this.server.tool(
418→      NX_GENERATOR_SCHEMA,
419→      'Returns the detailed JSON schema for an nx generator',
420→      {
421→        generatorName: z
422→          .string()
423→          .describe(
424→            'The name of the generator to get schema for. Use the generator name from the nx_generators tool.',
425→          ),
426→      },
427→      {
428→        destructiveHint: false,
429→        readOnlyHint: true,
430→        openWorldHint: false,
431→      },
432→      async ({ generatorName }) => {
433→        this.telemetry?.logUsage('ai.tool-call', {
434→          tool: NX_GENERATOR_SCHEMA,
435→        });
436→        if (!this._nxWorkspacePath) {
437→          return {
438→            isError: true,
439→            content: [{ type: 'text', text: 'Error: Workspace path not set' }],
440→          };
441→        }
442→        const generators = await this.nxWorkspaceInfoProvider.getGenerators(
443→          this._nxWorkspacePath,
444→          undefined,
445→          this.logger,
446→        );
447→        if (!generators) {
448→          return {
449→            content: [{ type: 'text', text: 'No generators found' }],
450→          };
451→        }
452→        const generatorDetails = await getGeneratorSchema(
453→          generatorName,
454→          generators,
455→        );
456→
457→        let examples = '';
458→        try {
459→          const examplesPath = path.join(
460→            generators.find((g) => g.name === generatorName)?.schemaPath ?? '',
461→            '..',
462→            'examples.md',
463→          );
464→          examples = await readFile(examplesPath, 'utf-8');
465→        } catch (e) {
466→          examples = 'No examples available';
467→        }
468→
469→        return {
470→          content: [
471→            {
472→              type: 'text',
473→              text: `
474→Found generator schema for ${generatorName}: ${JSON.stringify(
475→                generatorDetails,
476→              )}.
477→              ${
478→                this.ideProvider
479→                  ? `Follow up by using the nx_run_generator tool. When generating libraries, apps or components, use the cwd option to specify the parent directory where you want to create the item.`
480→                  : `**IMPORTANT FIRST STEP**: When generating libraries, apps, or components:
481→
482→1. FIRST navigate to the parent directory where you want to create the item:
483→- Example: 'cd libs/shared' to create a library in libs/shared
484→
485→2. THEN run the generate command using the positional arg for the name & directory:
486→- Example: 'nx generate @nx/js:library library-name' instead of using --name or --directory
487→
488→3. AVOID using --directory flag when possible as it can lead to path confusion
489→- Use 'cd' to change directories and specify the positional arg instead
490→
491→This approach provides better clarity about where new code will be generated
492→and follows the Nx workspace convention for project organization.`
493→              }
494→
495→            `,
496→            },
497→            {
498→              type: 'text',
499→              text: 'Examples: \n' + examples,
500→            },
501→          ],
502→        };
503→      },
504→    );
505→
506→    if (
507→      (await this.nxWorkspaceInfoProvider.isNxCloudEnabled()) &&
508→      this._nxWorkspacePath
509→    ) {
510→      registerNxCloudTools(
511→        this._nxWorkspacePath,
512→        this.server,
513→        this.logger,
514→        this.telemetry,
515→        this.nxWorkspaceInfoProvider.getGitDiffs,
516→      );
517→    }
518→
519→    if (this._nxWorkspacePath) {
520→      registerNxTaskTools(
521→        this._nxWorkspacePath,
522→        this.server,
523→        this.logger,
524→        this.telemetry,
525→      );
526→    }
527→  }
528→
529→  private registerIdeTools(): void {
530→    this.server.tool(
531→      NX_VISUALIZE_GRAPH,
532→      'Visualize the Nx graph. This can show either a project graph or a task graph depending on the parameters. Use this to help users understand project dependencies or task dependencies. There can only be one graph visualization open at a time so avoid similar tool calls unless the user specifically requests it.',
533→      {
534→        visualizationType: z
535→          .enum(['project', 'project-task', 'full-project-graph'])
536→          .describe(
537→            'The way in which to visualize the graph. "project" shows the project graph focused on a single project. "project-task" shows the task graph focused on a specific task for a specific project. "full-project-graph" shows the full project graph for the entire repository.',
538→          ),
539→        projectName: z
540→          .string()
541→          .optional()
542→          .describe(
543→            'The name of the project to focus the graph on. Only used if visualizationType is "project" or "project-task".',
544→          ),
545→        taskName: z
546→          .string()
547→          .optional()
548→          .describe(
549→            'The name of the task to focus the graph on. Only used if visualizationType is "project-task".',
550→          ),
551→      },
552→      {
553→        readOnlyHint: false,
554→        openWorldHint: false,
555→      },
556→      async ({ visualizationType, projectName, taskName }) => {
557→        this.telemetry?.logUsage('ai.tool-call', {
558→          tool: NX_VISUALIZE_GRAPH,
559→          kind: visualizationType,
560→        });
561→
562→        if (this.ideProvider) {
563→          switch (visualizationType) {
564→            case 'project':
565→              if (!projectName) {
566→                return {
567→                  isError: true,
568→                  content: [{ type: 'text', text: 'Project name is required' }],
569→                };
570→              }
571→              this.ideProvider.focusProject(projectName);
572→              return {
573→                content: [
574→                  {
575→                    type: 'text',
576→                    text: getProjectGraphVisualizationMessage(projectName),
577→                  },
578→                ],
579→              };
580→            case 'project-task':
581→              if (!taskName) {
582→                return {
583→                  isError: true,
584→                  content: [
585→                    {
586→                      type: 'text',
587→                      text: 'Task name is required for task graph visualization',
588→                    },
589→                  ],
590→                };
591→              }
592→              if (!projectName) {
593→                return {
594→                  isError: true,
595→                  content: [{ type: 'text', text: 'Project name is required' }],
596→                };
597→              }
598→              this.ideProvider.focusTask(projectName, taskName);
599→              return {
600→                content: [
601→                  {
602→                    type: 'text',
603→                    text: getTaskGraphVisualizationMessage(
604→                      projectName,
605→                      taskName,
606→                    ),
607→                  },
608→                ],
609→              };
610→            case 'full-project-graph':
611→              this.ideProvider.showFullProjectGraph();
612→              return {
613→                content: [
614→                  {
615→                    type: 'text',
616→                    text: getProjectGraphVisualizationMessage(),
617→                  },
618→                ],
619→              };
620→          }
621→        }
622→        return {
623→          isError: true,
624→          content: [{ type: 'text', text: 'No IDE provider available' }],
625→        };
626→      },
627→    );
628→
629→    this.server.tool(
630→      NX_RUN_GENERATOR,
631→      'Opens the generate ui with whatever options you provide prefilled. ALWAYS USE THIS to run nx generators. Use the nx_generators and nx_generator_schema tools to learn about the available options BEFORE using this tool. ALWAYS use this when the user wants to generate something and ALWAYS use this instead of running a generator directly via the CLI. You can also call this tool to overwrite the options for an existing generator invocation.',
632→      {
633→        generatorName: z.string().describe('The name of the generator to run'),
634→        options: z
635→          .record(z.string(), z.unknown())
636→          .describe('The options to pass to the generator'),
637→        cwd: z
638→          .string()
639→          .optional()
640→          .describe(
641→            'The current working directory to run the generator from. This is always relative to the workspace root. If not specified, the workspace root will be used.',
642→          ),
643→      },
644→      {
645→        readOnlyHint: false,
646→        openWorldHint: false,
647→        destructiveHint: false,
648→      },
649→      async ({ generatorName, options, cwd }) => {
650→        this.telemetry?.logUsage('ai.tool-call', {
651→          tool: NX_RUN_GENERATOR,
652→        });
653→        if (!this._nxWorkspacePath) {
654→          return {
655→            isError: true,
656→            content: [{ type: 'text', text: 'Error: Workspace path not set' }],
657→          };
658→        }
659→
660→        if (!this.ideProvider) {
661→          return {
662→            isError: true,
663→            content: [{ type: 'text', text: 'No IDE provider available' }],
664→          };
665→        }
666→        try {
667→          const logFileName = await this.ideProvider.openGenerateUi(
668→            generatorName,
669→            options ?? {},
670→            cwd,
671→          );
672→
673→          return {
674→            content: [
675→              {
676→                type: 'text',
677→                text: createGeneratorUiResponseMessage(
678→                  generatorName,
679→                  logFileName,
680→                ),
681→              },
682→            ],
683→          };
684→        } catch (e) {
685→          return {
686→            isError: true,
687→            content: [{ type: 'text', text: String(e) }],
688→          };
689→        }
690→      },
691→    );
692→  }
693→}
