  1→import { CallToolResult } from '@modelcontextprotocol/sdk/types.js';
  2→import { NxConsoleTelemetryLogger } from '@nx-console/shared-telemetry';
  3→import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
  4→import { Logger } from '@nx-console/shared-utils';
  5→import {
  6→  getNxCloudTerminalOutput,
  7→  getRecentCIPEData,
  8→  getPipelineExecutionsSearch,
  9→  getPipelineExecutionDetails,
 10→} from '@nx-console/shared-nx-cloud';
 11→import { z } from 'zod';
 12→import {
 13→  NX_CLOUD_CIPE_DETAILS,
 14→  NX_CLOUD_CIPE_FAILURE,
 15→  NX_CLOUD_PIPELINE_EXECUTIONS_SEARCH,
 16→  NX_CLOUD_PIPELINE_EXECUTIONS_DETAILS,
 17→} from '@nx-console/shared-llm-context/src/lib/tool-names';
 18→
 19→export function registerNxCloudTools(
 20→  workspacePath: string,
 21→  server: McpServer,
 22→  logger: Logger,
 23→  telemetry?: NxConsoleTelemetryLogger,
 24→  getGitDiffs?: (
 25→    workspacePath: string,
 26→    baseSha?: string,
 27→    headSha?: string,
 28→  ) => Promise<{ path: string; diffContent: string }[] | null>,
 29→) {
 30→  server.tool(
 31→    NX_CLOUD_CIPE_DETAILS,
 32→    'Returns a list of CIPE (CI pipeline execution) details for the current workspace and branch from Nx Cloud. This includes the status, and execution ID or link ID. If there are failed tasks, it will also include the task ID. If this returns text that contains "canceled", that means that there were no failures, and additional help and details are not needed.',
 33→    {
 34→      destructiveHint: false,
 35→      readOnlyHint: true,
 36→      openWorldHint: true,
 37→    },
 38→    nxCloudCipeDetails(workspacePath, logger, telemetry),
 39→  );
 40→  server.tool(
 41→    NX_CLOUD_CIPE_FAILURE,
 42→    'Returns details about the failure of a CI pipeline execution. When given a execution ID or link ID and a task ID, the terminal output and affected git files will be returned.',
 43→    nxCloudFixCipeSchema.shape,
 44→    {
 45→      destructiveHint: false,
 46→      readOnlyHint: true,
 47→      openWorldHint: true,
 48→    },
 49→    nxCloudCipeAffectedFilesAndTerminalOutput(
 50→      workspacePath,
 51→      logger,
 52→      telemetry,
 53→      getGitDiffs ?? (async () => null),
 54→    ),
 55→  );
 56→
 57→  // Pipeline Executions Search
 58→  server.tool(
 59→    NX_CLOUD_PIPELINE_EXECUTIONS_SEARCH,
 60→    'Search for pipeline executions in Nx Cloud. Returns a list of pipeline executions matching the search criteria.',
 61→    pipelineExecutionSearchSchema.shape,
 62→    {
 63→      destructiveHint: false,
 64→      readOnlyHint: true,
 65→      openWorldHint: true,
 66→    },
 67→    nxCloudPipelineExecutionsSearch(workspacePath, logger, telemetry),
 68→  );
 69→
 70→  // Pipeline Execution Details
 71→  server.tool(
 72→    NX_CLOUD_PIPELINE_EXECUTIONS_DETAILS,
 73→    'Get detailed information about a specific pipeline execution in Nx Cloud.',
 74→    pipelineExecutionDetailsSchema.shape,
 75→    {
 76→      destructiveHint: false,
 77→      readOnlyHint: true,
 78→      openWorldHint: true,
 79→    },
 80→    nxCloudPipelineExecutionDetails(workspacePath, logger, telemetry),
 81→  );
 82→}
 83→
 84→const nxCloudCipeDetails =
 85→  (
 86→    workspacePath: string,
 87→    logger: Logger,
 88→    telemetry: NxConsoleTelemetryLogger | undefined,
 89→  ) =>
 90→  async () => {
 91→    telemetry?.logUsage('ai.tool-call', {
 92→      tool: 'nx_cloud_cipe_details',
 93→    });
 94→
 95→    const recentData = await getRecentCIPEData(workspacePath, logger);
 96→
 97→    if (recentData.error) {
 98→      throw new Error(
 99→        `Error getting recent CIPE data: ${recentData.error.message}`,
100→      );
101→    }
102→
103→    const content: CallToolResult['content'] = [];
104→
105→    content.push({
106→      type: 'text',
107→      text: `Nx Cloud Workspace Url: ${recentData.workspaceUrl}`,
108→    });
109→
110→    if (recentData.info && recentData.info.length > 0) {
111→      content.push({
112→        type: 'text',
113→        text: `Recent CI Pipeline Executions:`,
114→      });
115→      for (const info of recentData.info) {
116→        content.push({
117→          type: 'text',
118→          text: `- ${info.cipeUrl} (CIPE Status: ${info.status})`,
119→        });
120→        for (const runGroup of info.runGroups) {
121→          content.push({
122→            type: 'text',
123→            text: `  -- Run Group: ${runGroup.runGroup} (Run Group Status: ${runGroup.status})`,
124→          });
125→          for (const run of runGroup.runs) {
126→            let runPrompt = '    --- Run';
127→            if (run.executionId) {
128→              runPrompt += ` Execution ID: ${run.executionId}`;
129→            }
130→            if (run.linkId) {
131→              runPrompt += ` Link ID: ${run.linkId}`;
132→            }
133→
134→            runPrompt += ` (Run Status: ${run.status})`;
135→
136→            content.push({
137→              type: 'text',
138→              text: runPrompt,
139→            });
140→            for (const task of run.failedTasks ?? []) {
141→              content.push({
142→                type: 'text',
143→                text: `      ---- Failed Task: ${task}`,
144→              });
145→            }
146→            if (run.status === 'CANCELED') {
147→              content.push({
148→                type: 'text',
149→                text: `      ---- Note: This run was canceled, indicating no failures occurred.`,
150→              });
151→            }
152→          }
153→        }
154→      }
155→    } else {
156→      content.push({
157→        type: 'text',
158→        text: `No recent CI pipeline executions found. This means that there were no recent runs in the last hour, or there are no runs on the current git branch`,
159→      });
160→    }
161→
162→    return {
163→      content,
164→    } satisfies CallToolResult;
165→  };
166→
167→const nxCloudFixCipeSchema = z.object({
168→  executionId: z
169→    .string()
170→    .optional()
171→    .describe('The execution ID of the run in the CI pipeline execution'),
172→  linkId: z
173→    .string()
174→    .optional()
175→    .describe('The link ID of the run in the CI pipeline execution'),
176→  taskId: z
177→    .string()
178→    .describe(
179→      'The task ID of the failed task, which is a nx task that was executed',
180→    ),
181→});
182→
183→type NxCloudFixCipeParams = z.infer<typeof nxCloudFixCipeSchema>;
184→const nxCloudCipeAffectedFilesAndTerminalOutput =
185→  (
186→    workspacePath: string,
187→    logger: Logger,
188→    telemetry: NxConsoleTelemetryLogger | undefined,
189→    getGitDiffs: (
190→      workspacePath: string,
191→      baseSha?: string,
192→      headSha?: string,
193→    ) => Promise<{ path: string; diffContent: string }[] | null>,
194→  ) =>
195→  async (params: NxCloudFixCipeParams): Promise<CallToolResult> => {
196→    telemetry?.logUsage('ai.tool-call', {
197→      tool: 'nx_cloud_fix_cipe_failure',
198→    });
199→
200→    if (!params.executionId && !params.linkId) {
201→      throw new Error('Execution ID or link ID is required');
202→    }
203→
204→    const content: CallToolResult['content'] = [];
205→
206→    const terminalOutput = await getNxCloudTerminalOutput(
207→      {
208→        taskId: params.taskId,
209→        ciPipelineExecutionId: params.executionId,
210→        linkId: params.linkId,
211→      },
212→      workspacePath,
213→      logger,
214→    );
215→
216→    if (terminalOutput.error) {
217→      throw new Error(terminalOutput.error);
218→    }
219→    content.push({
220→      type: 'text',
221→      text: `Terminal Output: ${terminalOutput.terminalOutput}`,
222→    });
223→
224→    try {
225→      const changedFiles = await getGitDiffs?.(workspacePath);
226→      if (changedFiles) {
227→        for (const file of changedFiles) {
228→          content.push({
229→            type: 'text',
230→            text: `Changed File: ${file.path}`,
231→          });
232→          content.push({
233→            type: 'text',
234→            text: `Diff: ${file.diffContent}`,
235→          });
236→        }
237→      }
238→    } catch (e) {
239→      logger.log(`Error getting git diffs: ${e}`);
240→      content.push({
241→        type: 'text',
242→        text: 'Unable to get git diffs',
243→      });
244→    }
245→
246→    return { content };
247→  };
