383→    telemetry?.logUsage('ai.tool-call', {
384→      tool: NX_CLOUD_PIPELINE_EXECUTIONS_SEARCH,
385→    });
386→
387→    const result = await getPipelineExecutionsSearch(
388→      workspacePath,
389→      logger,
390→      params as any,
391→    );
392→
393→    if (result.error) {
394→      throw new Error(
395→        `Error searching pipeline executions: ${result.error.message}`,
396→      );
397→    }
398→
399→    const content: CallToolResult['content'] = [];
400→
401→    if (result.data?.items && result.data.items.length > 0) {
402→      content.push({
403→        type: 'text',
404→        text: `Found ${result.data.items.length} pipeline executions:`,
405→      });
406→
407→      for (const execution of result.data.items) {
408→        content.push({
409→          type: 'text',
410→          text: `- Pipeline Execution ID: ${execution.id}`,
411→        });
412→        content.push({
413→          type: 'text',
414→          text: `  Branch: ${execution.branch}, Status: ${execution.status}`,
415→        });
416→        content.push({
417→          type: 'text',
418→          text: `  Created: ${new Date(execution.createdAtMs).toISOString()}`,
419→        });
420→        if (execution.vcsTitle) {
421→          content.push({
422→            type: 'text',
423→            text: `  Title: ${execution.vcsTitle}`,
424→          });
425→        }
426→        if (execution.author) {
427→          content.push({
428→            type: 'text',
429→            text: `  Author: ${execution.author}`,
430→          });
431→        }
432→      }
433→
434→      if (result.data.nextPageToken) {
435→        content.push({
436→          type: 'text',
437→          text: `Next page token: ${result.data.nextPageToken}`,
438→        });
439→      }
440→    } else {
441→      content.push({
442→        type: 'text',
443→        text: 'No pipeline executions found matching the criteria.',
444→      });
445→    }
446→
447→    return { content };
448→  };
