  1→import { checkIsNxWorkspace } from '@nx-console/shared-npm';
  2→import {
  3→  getNxWorkspacePath,
  4→  WorkspaceConfigurationStore,
  5→} from '@nx-console/vscode-configuration';
  6→import { getOutputChannel } from '@nx-console/vscode-output-channels';
  7→import { getTelemetry } from '@nx-console/vscode-telemetry';
  8→import {
  9→  ensureEditorDirExists,
 10→  getMcpJsonPath,
 11→  getNxMcpPort,
 12→  hasNxMcpEntry,
 13→  isInCursor,
 14→  isInVSCode,
 15→  isInWindsurf,
 16→  readMcpJson,
 17→  writeMcpJson,
 18→} from '@nx-console/vscode-utils';
 19→import {
 20→  commands,
 21→  ExtensionContext,
 22→  FileSystemWatcher,
 23→  Uri,
 24→  window,
 25→  workspace,
 26→} from 'vscode';
 27→import { AgentRulesManager } from './agent-rules-manager';
 28→import { McpWebServer } from './mcp-web-server';
 29→import { findAvailablePort } from './ports';
 30→
 31→let mcpJsonWatcher: FileSystemWatcher | null = null;
 32→let hasInitializedMcp = false;
 33→
 34→export function startMcpServer() {
 35→  const port = getNxMcpPort();
 36→  if (!port) {
 37→    return;
 38→  }
 39→  McpWebServer.Instance.startSkeletonMcpServer(port);
 40→}
 41→
 42→export function stopMcpServer() {
 43→  McpWebServer.Instance.stopMcpServer();
 44→}
 45→
 46→export async function updateMcpServerWorkspacePath(workspacePath: string) {
 47→  await McpWebServer.Instance.updateMcpServerWorkspacePath(workspacePath);
 48→}
 49→
 50→export async function initMcp(context: ExtensionContext) {
 51→  if (hasInitializedMcp) {
 52→    return;
 53→  }
 54→
 55→  commands.executeCommand('setContext', 'isInCursor', isInCursor());
 56→  commands.executeCommand('setContext', 'isInWindsurf', isInWindsurf());
 57→  commands.executeCommand('setContext', 'isInVSCode', isInVSCode());
 58→
 59→  if (!(await checkIsNxWorkspace(getNxWorkspacePath(), false))) {
 60→    return;
 61→  }
 62→  hasInitializedMcp = true;
 63→
 64→  commands.executeCommand('setContext', 'hasNxMcpConfigured', hasNxMcpEntry());
 65→
 66→  McpWebServer.Instance.completeMcpServerSetup();
 67→
 68→  const rulesManager = new AgentRulesManager(context);
 69→
 70→  context.subscriptions.push(
 71→    commands.registerCommand('nx.configureMcpServer', async () => {
 72→      await updateMcpJson();
 73→      await rulesManager.addAgentRulesToWorkspace();
 74→    }),
 75→    commands.registerCommand('nx.addAgentRules', async () => {
 76→      await rulesManager.addAgentRulesToWorkspace();
 77→    }),
 78→  );
 79→
 80→  await rulesManager.initialize();
 81→
 82→  setupMcpJsonWatcher(context);
 83→
 84→  // Wait a bit before showing notification
 85→  await new Promise((resolve) => setTimeout(resolve, 20000));
 86→  await showMCPNotification(rulesManager);
 87→}
 88→
 89→function setupMcpJsonWatcher(context: ExtensionContext) {
 90→  const mcpJsonPath = getMcpJsonPath();
 91→  if (!mcpJsonPath) {
 92→    return;
 93→  }
 94→
 95→  let lastPort = getNxMcpPort();
 96→
 97→  mcpJsonWatcher = workspace.createFileSystemWatcher(mcpJsonPath);
 98→
 99→  const handleMcpJsonChange = async (eventMessage: string) => {
100→    getOutputChannel().appendLine(eventMessage);
101→    const port = getNxMcpPort();
102→    if (port !== lastPort) {
103→      lastPort = port;
104→      McpWebServer.Instance.stopMcpServer();
105→      if (port) {
106→        McpWebServer.Instance.startSkeletonMcpServer(port);
107→      }
108→    }
109→
110→    commands.executeCommand(
111→      'setContext',
112→      'hasNxMcpConfigured',
113→      hasNxMcpEntry(),
114→    );
115→  };
116→
117→  mcpJsonWatcher.onDidChange(async () => {
118→    await handleMcpJsonChange('mcp.json file changed, updating server port');
119→  });
120→
121→  mcpJsonWatcher.onDidCreate(async () => {
122→    await handleMcpJsonChange('mcp.json file created, updating server port');
123→  });
124→
125→  mcpJsonWatcher.onDidDelete(async () => {
126→    await handleMcpJsonChange('mcp.json file deleted, updating server port');
127→  });
128→
129→  context.subscriptions.push(mcpJsonWatcher);
130→}
131→
132→async function showMCPNotification(rulesManager: AgentRulesManager) {
133→  const dontAskAgain = WorkspaceConfigurationStore.instance.get(
134→    'mcpDontAskAgain',
135→    false,
136→  );
137→
138→  if (dontAskAgain) {
139→    return;
140→  }
141→
142→  if (isInWindsurf()) {
143→    // TODO: do once windsurf supports project-level mcp servers
144→    return;
145→  }
146→
147→  if (hasNxMcpEntry()) {
148→    // if mcp is already configured but the rules file isn't, prompt for rules setup
149→    await rulesManager.showAgentRulesNotification();
150→    return;
151→  }
152→
153→  const msg = isInCursor()
154→    ? 'Improve Cursor Agents with Nx-specific context?'
155→    : isInWindsurf()
156→      ? 'Improve Cascade with Nx-specific context?'
157→      : 'Improve Copilot Agents with Nx-specific context?';
158→
159→  window
160→    .showInformationMessage(msg, 'Yes', "Don't ask again")
161→    .then(async (answer) => {
162→      if (answer === "Don't ask again") {
163→        WorkspaceConfigurationStore.instance.set('mcpDontAskAgain', true);
164→      }
165→
166→      if (answer === 'Yes') {
167→        await updateMcpJson();
168→        await rulesManager.addAgentRulesToWorkspace();
169→      }
170→    });
171→}
172→
173→async function updateMcpJson() {
174→  if (!ensureEditorDirExists()) {
175→    return false;
176→  }
177→
178→  if (hasNxMcpEntry()) {
179→    return true;
180→  }
181→
182→  getTelemetry().logUsage('ai.add-mcp');
183→
184→  const port = await findAvailablePort();
185→  if (!port) {
186→    window.showErrorMessage(
187→      'Failed to find an available port for MCP SSE server',
188→    );
189→    return false;
190→  }
191→
192→  const mcpJson =
193→    readMcpJson() || (isInCursor() ? { mcpServers: {} } : { servers: {} });
194→
195→  if (isInCursor()) {
196→    if (!mcpJson.mcpServers) {
197→      mcpJson.mcpServers = {};
198→    }
199→
200→    mcpJson.mcpServers['nx-mcp'] = {
