  7→import {
  8→  getGenerators,
  9→  nxWorkspace,
 10→} from '@nx-console/shared-nx-workspace-info';
 11→import {
 12→  GoogleAnalytics,
 13→  NxConsoleTelemetryLogger,
 14→} from '@nx-console/shared-telemetry';
 15→import { randomUUID } from 'crypto';
 16→import yargs from 'yargs';
 17→import { hideBin } from 'yargs/helpers';
 18→import express from 'express';
 19→import { isNxCloudUsed } from '@nx-console/shared-nx-cloud';
 20→import { checkIsNxWorkspace } from '@nx-console/shared-npm';
 21→import { resolve } from 'path';
 22→
 23→interface ArgvType {
 24→  workspacePath?: string;
 25→  sse: boolean;
 26→  port?: number;
 27→  disableTelemetry: boolean;
 28→  keepAliveInterval: number;
 29→  _: (string | number)[];
 30→  $0: string;
 31→  [x: string]: unknown;
 32→}
 33→
 34→async function main() {
 35→  const argv = yargs(hideBin(process.argv))
 36→    .command('$0 [workspacePath]', 'Start the nx-mcp server', (yargs) => {
 37→      yargs.positional('workspacePath', {
 38→        describe: 'Path to the Nx workspace root',
 39→        type: 'string',
 40→      });
 41→    })
 42→    .option('workspacePath', {
 43→      alias: 'w',
 44→      describe: 'Path to the Nx workspace root',
 45→      type: 'string',
 46→    })
 47→    .option('sse', {
 48→      describe: 'Configure the server to use SSE (Server-Sent Events)',
 49→      type: 'boolean',
 50→      default: false,
 51→    })
 52→    .option('port', {
 53→      alias: 'p',
 54→      describe: 'Port to use for the SSE server (default: 9921)',
 55→      type: 'number',
 56→    })
 57→    .option('disableTelemetry', {
 58→      describe: 'Disable sending of telemetry data',
 59→      type: 'boolean',
 60→      default: false,
 61→    })
 62→    .option('keepAliveInterval', {
 63→      describe:
 64→        'Interval in milliseconds to send SSE keep-alive messages (default: 30000, 0 to disable)',
 65→      type: 'number',
 66→      default: 30000,
 67→    })
 68→    .check((argv) => {
 69→      if (argv.port !== undefined && !argv.sse) {
 70→        throw new Error(
 71→          'The --port option can only be used when --sse is enabled',
 72→        );
 73→      }
 74→      return true;
 75→    })
 76→    .help()
 77→    .parseSync() as ArgvType;
 78→
 79→  const providedPath: string = resolve(
 80→    argv.workspacePath || (argv._[0] as string) || process.cwd(),
 81→  ) as string;
 82→
 83→  // Check if the provided path is an Nx workspace
 84→  const isNxWorkspace = await checkIsNxWorkspace(providedPath);
 85→  const nxWorkspacePath = isNxWorkspace ? providedPath : undefined;
 86→
 87→  let googleAnalytics: GoogleAnalytics | undefined;
 88→
 89→  if (!argv.disableTelemetry) {
 90→    const clientId = randomUUID();
 91→    googleAnalytics = new GoogleAnalytics(
 92→      'production',
 93→      clientId,
 94→      clientId,
 95→      clientId,
 96→      getPackageVersion(),
 97→      'nx-mcp',
 98→    );
 99→  }
100→
101→  const telemetryLogger: NxConsoleTelemetryLogger = {
102→    logUsage: (eventName, data) => {
103→      googleAnalytics?.sendEventData(eventName, data);
104→    },
105→  };
106→
107→  const nxWorkspaceInfoProvider: NxWorkspaceInfoProvider = {
108→    nxWorkspace,
109→    getGenerators,
110→    getGitDiffs: async () => {
111→      // todo(cammisuli): implement this using standard git commands
112→      return undefined;
113→    },
114→    isNxCloudEnabled: async () =>
115→      nxWorkspacePath ? await isNxCloudUsed(nxWorkspacePath) : false,
116→  };
117→
118→  const server = await NxMcpServerWrapper.create(
119→    nxWorkspacePath,
120→    nxWorkspaceInfoProvider,
121→    undefined,
122→    telemetryLogger,
123→  );
124→
125→  if (argv.sse) {
126→    const port = argv.port ?? 9921;
127→
128→    const app = express();
129→    let transport: SSEServerTransport;
130→    app.get('/sse', async (req, res) => {
131→      console.log('Configuring SSE transport');
132→      transport = new SSEServerTransport('/messages', res);
133→      await server.getMcpServer().connect(transport);
134→
135→      // Set up keep-alive interval if enabled
136→      if (argv.keepAliveInterval > 0) {
137→        const keepAliveInterval = setInterval(() => {
138→          // Check if the connection is still open using the socket's writable state
139→          if (!res.writableEnded && !res.writableFinished) {
140→            res.write(':beat\n\n');
141→          } else {
142→            clearInterval(keepAliveInterval);
143→            console.log('SSE connection closed, clearing keep-alive interval');
144→          }
145→        }, argv.keepAliveInterval);
146→
147→        // Clean up interval if the client disconnects
148→        req.on('close', () => {
149→          clearInterval(keepAliveInterval);
150→          console.log('SSE connection closed by client');
151→        });
152→      }
153→    });
154→
155→    app.post('/messages', async (req, res) => {
156→      if (!transport) {
157→        console.log('No transport found.');
158→        res.status(400).send('No transport found');
159→        return;
160→      }
161→      await transport.handlePostMessage(req, res);
162→    });
163→
164→    const server_instance = app.listen(port);
165→
166→    process.on('exit', () => {
167→      server_instance.close();
168→    });
169→    console.log(`Nx MCP server listening on port ${port}`);
170→  } else {
171→    const transport = new StdioServerTransport();
172→    server.getMcpServer().connect(transport);
173→  }
174→
175→  function getPackageVersion() {
176→    try {
177→      // eslint-disable-next-line @typescript-eslint/no-var-requires
178→      return require('./package.json').version;
179→    } catch {
180→      return '0.0.0';
181→    }
182→  }
183→
184→  // Prevent the Node.js process from exiting early
185→  process.on('SIGINT', () => {
186→    console.log('Received SIGINT signal. Server shutting down...');
187→    process.exit(0);
188→  });
189→
190→  // Keep the process alive
191→  process.stdin.resume();
192→}
193→
194→main().catch((err) => {
195→  console.error('Fatal error:', err);
196→  process.exit(1);
197→});
