import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
import {
  createGeneratorUiResponseMessage,
  getDocsContext,
  getDocsPrompt,
  getGeneratorNamesAndDescriptions,
  getGeneratorSchema,
  getGeneratorsPrompt,
  getNxJsonPrompt,
  getPluginsInformation,
  getProjectGraphErrorsPrompt,
