import { prompt } from 'enquirer';
import { homedir } from 'os';
import { output } from './output';
import { NxConsolePreferences } from '../native';

export interface NxConsolePromptOptions {
  /**
   * Whether to show the prompt interactively
   * @default true
   */
  interactive?: boolean;

  /**
   * Custom home directory path
   * @default os.homedir()
   */
  homeDir?: string;

  /**
   * Whether to force showing the prompt even if preference is already set
   * @default false
   */
  force?: boolean;
}

/**
 * Prompts the user whether they want to automatically install the Nx Console extension
 * and persists their preference using the NxConsolePreferences struct
 */
export async function promptForNxConsoleInstallation(
  options: NxConsolePromptOptions = {}
): Promise<boolean> {
  const { 
    interactive = true, 
    homeDir = homedir(), 
    force = false 
  } = options;

  const preferences = new NxConsolePreferences(homeDir);

  // Check if preference is already set and we're not forcing the prompt
  if (!force && preferences.autoInstallConsole !== undefined) {
    return preferences.wantsAutoInstall();
  }

  // Skip prompt if not in interactive mode
  if (!interactive) {
    return false;
  }

  try {
    output.log({
      title: 'Nx Console Extension',
      bodyLines: [
        'Nx Console is a powerful extension for VS Code and other editors that provides:',
        ' Visual project graph exploration',
        ' Interactive code generation',
        ' Task execution with terminal integration',
        ' IntelliSense for Nx configuration files',
        '',
        'Would you like Nx to automatically install/update the Nx Console extension',
        'when it detects a supported editor?'
      ]
    });

    const response = await prompt<{ installConsole: boolean }>({
      type: 'confirm',
      name: 'installConsole',
      message: 'Enable automatic Nx Console extension installation?',
      initial: true
    });

    // Persist the user's preference
    preferences.setAutoInstallPreference(response.installConsole);

    if (response.installConsole) {
      output.success({
        title: 'Nx Console auto-installation enabled',
        bodyLines: [
          'Nx will now automatically install the Nx Console extension when it',
          'detects VS Code, Windsurf, or other supported editors.',
          '',
          'You can disable this anytime by setting NX_SKIP_VSCODE_EXTENSION_INSTALL=true'
        ]
      });
    } else {
      output.note({
        title: 'Nx Console auto-installation disabled',
        bodyLines: [
          'You can manually install the Nx Console extension by searching for',
          '"Nx Console" in your editor\'s extension marketplace, or enable',
          'auto-installation later by running: nx g @nx/workspace:setup-ide'
        ]
      });
    }

    return response.installConsole;

  } catch (error) {
    // Handle prompt cancellation (Ctrl+C) gracefully
    if (error.message?.includes('cancelled')) {
      output.note({
        title: 'Setup cancelled',
        bodyLines: ['Nx Console installation preference not changed.']
      });
      return false;
    }

    // Re-throw other errors
    throw error;
  }
}

/**
 * Utility function to check if the user wants auto-installation
 * without prompting (reads from saved preferences)
 */
export function checkNxConsoleAutoInstallPreference(homeDir?: string): boolean {
  const preferences = new NxConsolePreferences(homeDir || homedir());
  return preferences.wantsAutoInstall();
}

/**
 * Utility function to programmatically set the auto-install preference
 */
export function setNxConsoleAutoInstallPreference(
  enabled: boolean, 
  homeDir?: string
): void {
  const preferences = new NxConsolePreferences(homeDir || homedir());
  preferences.setAutoInstallPreference(enabled);
}

