 173	function projectExists(
 174	  projects: ProjectGraphProjectNode[],
 175	  projectToFind: string
 176	) {
 177	  return (
 178	    projects.find((project) => project.name === projectToFind) !== undefined
 179	  );
 180	}
 181	
 182	function hasPath(
 183	  graph: ProjectGraph,
 184	  target: string,
 185	  node: string,
 186	  visited: string[]
 187	) {
 188	  if (target === node) return true;
 189	
 190	  for (let d of graph.dependencies[node] || []) {
 191	    if (visited.indexOf(d.target) > -1) continue;
 192	    visited.push(d.target);
 193	    if (hasPath(graph, target, d.target, visited)) return true;
 194	  }
 195	  return false;
 196	}
 197	
 198	function filterGraph(
 199	  graph: ProjectGraph,
 200	  focus: string,
 201	  exclude: string[]
 202	): ProjectGraph {
 203	  let projectNames = (
 204	    Object.values(graph.nodes) as ProjectGraphProjectNode[]
 205	  ).map((project) => project.name);
 206	
 207	  let filteredProjectNames: Set<string>;
 208	
 209	  if (focus !== null) {
 210	    filteredProjectNames = new Set<string>();
 211	    projectNames.forEach((p) => {
 212	      const isInPath =
 213	        hasPath(graph, p, focus, []) || hasPath(graph, focus, p, []);
 214	
 215	      if (isInPath) {
 216	        filteredProjectNames.add(p);
 217	      }
 218	    });
 219	  } else {
 220	    filteredProjectNames = new Set<string>(projectNames);
 221	  }
 222	
 223	  if (exclude.length !== 0) {
 224	    exclude.forEach((p) => filteredProjectNames.delete(p));
 225	  }
 226	
 227	  let filteredGraph: ProjectGraph = {
 228	    nodes: {},
 229	    dependencies: {},
 230	  };
 231	
 232	  filteredProjectNames.forEach((p) => {
 233	    filteredGraph.nodes[p] = graph.nodes[p];
 234	    filteredGraph.dependencies[p] = graph.dependencies[p];
 235	  });
 236	
 237	  return filteredGraph;
 238	}
