 240	export async function generateGraph(
 241	  args: {
 242	    file?: string;
 243	    host?: string;
 244	    port?: number;
 245	    groupByFolder?: boolean;
 246	    watch?: boolean;
 247	    open?: boolean;
 248	    view: 'projects' | 'tasks' | 'project-details';
 249	    projects?: string[];
 250	    all?: boolean;
 251	    targets?: string[];
 252	    focus?: string;
 253	    exclude?: string[];
 254	    affected?: boolean;
 255	  },
 256	  affectedProjects: string[]
 257	): Promise<void> {
 258	  if (
 259	    Array.isArray(args.targets) &&
 260	    args.targets.length > 1 &&
 261	    args.file &&
 262	    !(args.file === 'stdout' || args.file.endsWith('.json'))
 263	  ) {
 264	    output.warn({
 265	      title: 'Showing Multiple Targets is not supported yet',
 266	      bodyLines: [
 267	        `Only the task graph for "${args.targets[0]}" tasks will be shown`,
 268	      ],
 269	    });
 270	  }
 271	
 272	  if (args.view === 'project-details' && !args.focus) {
 273	    output.error({
 274	      title: `The project details view requires the --focus option.`,
 275	    });
 276	    process.exit(1);
 277	  }
 278	  if (args.view === 'project-details' && (args.targets || args.affected)) {
 279	    output.error({
 280	      title: `The project details view can only be used with the --focus option.`,
 281	      bodyLines: [
 282	        `You passed ${args.targets ? '--targets ' : ''}${
 283	          args.affected ? '--affected ' : ''
 284	        }`,
 285	      ],
 286	    });
 287	    process.exit(1);
 288	  }
 289	
 290	  // TODO: Graph Client should support multiple targets
 291	  const target = Array.isArray(args.targets && args.targets.length >= 1)
 292	    ? args.targets[0]
 293	    : args.targets;
 294	
 295	  let rawGraph: ProjectGraph;
 296	  let sourceMaps: ConfigurationSourceMaps;
 297	  let isPartial = false;
 298	  try {
 299	    const projectGraphAndSourceMaps =
 300	      await createProjectGraphAndSourceMapsAsync({
 301	        exitOnError: false,
 302	      });
 303	    rawGraph = projectGraphAndSourceMaps.projectGraph;
 304	    sourceMaps = projectGraphAndSourceMaps.sourceMaps;
 305	  } catch (e) {
 306	    if (e instanceof ProjectGraphError) {
 307	      rawGraph = e.getPartialProjectGraph();
 308	      sourceMaps = e.getPartialSourcemaps();
 309	
 310	      isPartial = true;
 311	    }
 312	    if (!rawGraph) {
 313	      handleProjectGraphError({ exitOnError: true }, e);
 314	    } else {
 315	      const errors = e.getErrors();
 316	      if (errors?.length > 0) {
 317	        errors.forEach((e) => {
 318	          output.error({
 319	            title: e.message,
 320	            bodyLines: [e.stack],
 321	          });
 322	        });
 323	      }
 324	      output.warn({
 325	        title: `${
 326	          errors?.length > 1 ? `${errors.length} errors` : `An error`
 327	        } occured while processing the project graph. Showing partial graph.`,
 328	      });
 329	    }
 330	  }
 331	  let prunedGraph = pruneExternalNodes(rawGraph);
 332	
 333	  const projects = Object.values(
 334	    prunedGraph.nodes
 335	  ) as ProjectGraphProjectNode[];
 336	  projects.sort((a, b) => {
 337	    return a.name.localeCompare(b.name);
 338	  });
 339	
 340	  if (args.focus) {
 341	    if (!projectExists(projects, args.focus)) {
 342	      output.error({
 343	        title: `Project to focus does not exist.`,
 344	        bodyLines: [`You provided --focus=${args.focus}`],
 345	      });
 346	      process.exit(1);
 347	    }
 348	  }
 349	
 350	  if (args.affected) {
 351	    affectedProjects = (
 352	      await getAffectedGraphNodes(
 353	        splitArgsIntoNxArgsAndOverrides(
 354	          args,
 355	          'affected',
 356	          { printWarnings: args.file !== 'stdout' },
 357	          readNxJson()
 358	        ).nxArgs,
 359	        rawGraph
 360	      )
 361	    ).map((n) => n.name);
 362	  }
 363	
 364	  if (args.exclude) {
 365	    const invalidExcludes: string[] = [];
 366	
 367	    args.exclude.forEach((project) => {
 368	      if (!projectExists(projects, project)) {
 369	        invalidExcludes.push(project);
 370	      }
 371	    });
 372	
 373	    if (invalidExcludes.length > 0) {
 374	      output.error({
 375	        title: `The following projects provided to --exclude do not exist:`,
 376	        bodyLines: invalidExcludes,
 377	      });
 378	      process.exit(1);
 379	    }
 380	  }
 381	
 382	  let html = readFileSync(
 383	    join(__dirname, '../../core/graph/index.html'),
 384	    'utf-8'
 385	  );
 386	
 387	  prunedGraph = filterGraph(
 388	    prunedGraph,
 389	    args.focus || null,
 390	    args.exclude || []
 391	  );
 392	
 393	  if (args.file) {
 394	    // stdout is a magical constant that doesn't actually write a file
 395	    if (args.file === 'stdout') {
 396	      console.log(
 397	        JSON.stringify(
 398	          await createJsonOutput(
 399	            prunedGraph,
 400	            rawGraph,
 401	            args.projects,
 402	            args.targets
 403	          ),
 404	          null,
 405	          2
 406	        )
 407	      );
 408	      await output.drain();
 409	      process.exit(0);
 410	    }
 411	
 412	    const workspaceFolder = workspaceRoot;
 413	    const ext = extname(args.file);
 414	    const fullFilePath = isAbsolute(args.file)
 415	      ? args.file
 416	      : join(workspaceFolder, args.file);
 417	    const fileFolderPath = dirname(fullFilePath);
 418	
 419	    if (ext === '.html') {
 420	      const assetsFolder = join(fileFolderPath, 'static');
 421	      const assets: string[] = [];
 422	      cpSync(join(__dirname, '../../core/graph'), assetsFolder, {
 423	        filter: (_src, dest) => {
