 432	
 433	      const { projectGraphClientResponse } =
 434	        await createProjectGraphAndSourceMapClientResponse(affectedProjects);
 435	
 436	      const taskGraphClientResponse = await createTaskGraphClientResponse();
 437	      const taskInputsReponse = await createExpandedTaskInputResponse(
