 439	        projectGraphClientResponse
 440	      );
 441	
 442	      const environmentJs = buildEnvironmentJs(
 443	        args.exclude || [],
 444	        args.watch,
 445	        !!args.file && args.file.endsWith('html') ? 'build' : 'serve',
 446	        projectGraphClientResponse,
 447	        taskGraphClientResponse,
 448	        taskInputsReponse,
 449	        sourceMaps
 450	      );
 451	      html = html.replace(/src="/g, 'src="static/');
 452	      html = html.replace(/href="styles/g, 'href="static/styles');
 453	      html = html.replace(/<base href="\/".*>/g, '');
 454	      html = html.replace(/type="module"/g, '');
 455	
 456	      writeFileSync(fullFilePath, html);
 457	      writeFileSync(join(assetsFolder, 'environment.js'), environmentJs);
 458	
 459	      output.success({
 460	        title: `HTML output created in ${fileFolderPath}`,
 461	        bodyLines: [fileFolderPath, ...assets],
 462	      });
 463	    } else if (ext === '.json') {
 464	      mkdirSync(dirname(fullFilePath), { recursive: true });
 465	
 466	      const json = await createJsonOutput(
 467	        prunedGraph,
 468	        rawGraph,
 469	        args.projects,
 470	        args.targets
 471	      );
 472	
 473	      writeJsonFile(fullFilePath, json);
 474	
 475	      output.success({
 476	        title: `JSON output created in ${fileFolderPath}`,
 477	        bodyLines: [fullFilePath],
 478	      });
 479	    } else {
 480	      output.error({
 481	        title: `Please specify a filename with either .json or .html extension.`,
 482	        bodyLines: [`You provided --file=${args.file}`],
 483	      });
 484	      process.exit(1);
 485	    }
 486	    process.exit(0);
 487	  } else {
 488	    const environmentJs = buildEnvironmentJs(
 489	      args.exclude || [],
 490	      args.watch,
 491	      !!args.file && args.file.endsWith('html') ? 'build' : 'serve'
 492	    );
 493	
 494	    let app: Server;
 495	    let url: URL;
 496	    try {
 497	      const result = await startServer(
 498	        html,
 499	        environmentJs,
 500	        args.host || '127.0.0.1',
 501	        args.port || 4211,
 502	        args.watch,
 503	        affectedProjects,
 504	        args.focus,
 505	        args.groupByFolder,
 506	        args.exclude
 507	      );
 508	      app = result.app;
 509	      url = result.url;
 510	    } catch (err) {
 511	      output.error({
 512	        title: 'Failed to start graph server',
 513	        bodyLines: [err.message],
 514	      });
 515	      process.exit(1);
 516	    }
 517	
 518	    url.pathname = args.view;
 519	
 520	    if (args.focus) {
 521	      url.pathname += '/' + encodeURIComponent(args.focus);
 522	    }
 523	
 524	    if (target) {
 525	      url.pathname += '/' + target;
 526	    }
 527	    if (args.all) {
 528	      url.pathname += '/all';
 529	    } else if (args.projects) {
 530	      url.searchParams.append(
 531	        'projects',
 532	        args.projects.map((projectName) => projectName).join(' ')
