 541	    output.success({
 542	      title: `Project graph started at ${url.toString()}`,
 543	    });
 544	
 545	    if (args.open) {
 546	      open(url.toString());
 547	    }
 548	
 549	    return new Promise((res) => {
 550	      app.once('close', res);
 551	    });
 552	  }
 553	}
