 555	function findAvailablePort(
 556	  startPort: number,
 557	  host: string = '127.0.0.1'
 558	): Promise<number> {
 559	  return new Promise((resolve, reject) => {
 560	    const server = net.createServer();
 561	
 562	    server.listen(startPort, host, () => {
 563	      const port = (server.address() as net.AddressInfo).port;
 564	      server.close(() => {
 565	        resolve(port);
 566	      });
 567	    });
 568	
 569	    server.on('error', (err: NodeJS.ErrnoException) => {
 570	      if (err.code === 'EADDRINUSE') {
 571	        // Port is in use, try the next one
 572	        findAvailablePort(startPort + 1, host)
 573	          .then(resolve)
 574	          .catch(reject);
 575	      } else {
 576	        reject(err);
 577	      }
 578	    });
 579	  });
 580	}
 581	
 582	async function startServer(
 583	  html: string,
 584	  environmentJs: string,
 585	  host: string,
 586	  port = 4211,
 587	  watchForChanges = true,
 588	  affected: string[] = [],
 589	  focus: string = null,
 590	  groupByFolder: boolean = false,
 591	  exclude: string[] = []
 592	) {
 593	  let unregisterFileWatcher: (() => void) | undefined;
 594	
 595	  if (watchForChanges && !daemonClient.enabled()) {
 596	    output.warn({
 597	      title:
 598	        'Nx Daemon is not enabled. Graph will not refresh on file changes.',
 599	    });
 600	  }
 601	
 602	  if (watchForChanges && daemonClient.enabled()) {
 603	    unregisterFileWatcher = await createFileWatcher();
 604	  }
 605	
 606	  const { projectGraphClientResponse, sourceMapResponse } =
 607	    await createProjectGraphAndSourceMapClientResponse(affected);
 608	
 609	  currentProjectGraphClientResponse = projectGraphClientResponse;
 610	  currentProjectGraphClientResponse.focus = focus;
 611	  currentProjectGraphClientResponse.groupByFolder = groupByFolder;
 612	  currentProjectGraphClientResponse.exclude = exclude;
 613	
 614	  currentSourceMapsClientResponse = sourceMapResponse;
 615	
 616	  const app = http.createServer(async (req, res) => {
 617	    // parse URL
 618	    const parsedUrl = new URL(req.url, `http://${host}:${port}`);
 619	    // extract URL path
 620	    // Avoid https://en.wikipedia.org/wiki/Directory_traversal_attack
 621	    // e.g curl --path-as-is http://localhost:9000/../fileInDanger.txt
 622	    // by limiting the path to current directory only
 623	
 624	    res.setHeader('Access-Control-Allow-Origin', '*');
 625	
 626	    const sanitizePath = basename(parsedUrl.pathname);
 627	    if (sanitizePath === 'project-graph.json') {
 628	      res.writeHead(200, { 'Content-Type': 'application/json' });
 629	      res.end(JSON.stringify(currentProjectGraphClientResponse));
 630	      return;
 631	    }
 632	
 633	    if (sanitizePath === 'task-graph.json') {
 634	      res.writeHead(200, { 'Content-Type': 'application/json' });
 635	      res.end(JSON.stringify(await createTaskGraphClientResponse()));
 636	      return;
 637	    }
 638	
 639	    if (sanitizePath === 'task-inputs.json') {
 640	      performance.mark('task input generation:start');
 641	
 642	      const taskId = parsedUrl.searchParams.get('taskId');
 643	      res.writeHead(200, { 'Content-Type': 'application/json' });
 644	      const inputs = await getExpandedTaskInputs(taskId);
 645	      performance.mark('task input generation:end');
 646	
 647	      res.end(JSON.stringify({ [taskId]: inputs }));
 648	      performance.measure(
 649	        'task input generation',
 650	        'task input generation:start',
 651	        'task input generation:end'
 652	      );
 653	
 654	      return;
 655	    }
 656	
 657	    if (sanitizePath === 'source-maps.json') {
 658	      res.writeHead(200, { 'Content-Type': 'application/json' });
 659	      res.end(JSON.stringify(currentSourceMapsClientResponse));
 660	      return;
 661	    }
 662	
 663	    if (sanitizePath === 'currentHash') {
 664	      res.writeHead(200, { 'Content-Type': 'application/json' });
 665	      res.end(JSON.stringify({ hash: currentProjectGraphClientResponse.hash }));
 666	      return;
 667	    }
 668	
 669	    if (sanitizePath === 'environment.js') {
 670	      res.writeHead(200, { 'Content-Type': 'application/javascript' });
 671	      res.end(environmentJs);
 672	      return;
 673	    }
 674	
 675	    if (sanitizePath === 'help') {
 676	      const project = parsedUrl.searchParams.get('project');
 677	      const target = parsedUrl.searchParams.get('target');
 678	
 679	      try {
 680	        const text = getHelpTextFromTarget(project, target);
 681	        res.writeHead(200, { 'Content-Type': 'application/javascript' });
 682	        res.end(JSON.stringify({ text, success: true }));
 683	      } catch (err) {
 684	        res.writeHead(200, { 'Content-Type': 'application/javascript' });
 685	        res.end(JSON.stringify({ text: err.message, success: false }));
 686	      }
 687	      return;
 688	    }
 689	
 690	    let pathname = join(__dirname, '../../core/graph/', sanitizePath);
 691	    // if the file is not found or is a directory, return index.html
 692	    if (!existsSync(pathname) || statSync(pathname).isDirectory()) {
 693	      res.writeHead(200, { 'Content-Type': 'text/html' });
 694	      res.end(html);
 695	      return;
 696	    }
 697	
 698	    try {
 699	      const data = readFileSync(pathname);
 700	
 701	      const ext = parse(pathname).ext;
 702	      res.setHeader('Content-type', mimeType[ext] || 'text/plain');
 703	      res.end(data);
 704	    } catch (err) {
 705	      res.statusCode = 500;
 706	      res.end(`Error getting the file: ${err}.`);
 707	    }
 708	  });
 709	
 710	  const handleTermination = async (exitCode: number) => {
 711	    if (unregisterFileWatcher) {
 712	      unregisterFileWatcher();
 713	    }
 714	    process.exit(exitCode);
 715	  };
 716	  process.on('SIGINT', () => handleTermination(128 + 2));
 717	  process.on('SIGTERM', () => handleTermination(128 + 15));
 718	
 719	  // Find an available port starting from the requested port
 720	  const availablePort = await findAvailablePort(port, host);
 721	
 722	  return new Promise<{ app: Server; url: URL }>((res, rej) => {
 723	    app.on('error', (err: NodeJS.ErrnoException) => {
 724	      rej(err);
 725	    });
 726	
 727	    app.listen(availablePort, host, () => {
 728	      if (availablePort !== port) {
 729	        output.note({
 730	          title: `Port ${port} was already in use, using port ${availablePort} instead`,
 731	        });
 732	      }
 733	      res({ app, url: new URL(`http://${host}:${availablePort}`) });
 734	    });
 735	  });
 736	}
