 768	function createFileWatcher() {
 769	  return daemonClient.registerFileWatcher(
 770	    {
 771	      watchProjects: 'all',
 772	      includeGlobalWorkspaceFiles: true,
 773	      allowPartialGraph: true,
 774	    },
 775	    debounce(async (error, changes) => {
 776	      if (error === 'closed') {
 777	        output.error({ title: `Watch error: Daemon closed the connection` });
 778	        process.exit(1);
 779	      } else if (error) {
 780	        output.error({ title: `Watch error: ${error?.message ?? 'Unknown'}` });
 781	      } else if (changes !== null && changes.changedFiles.length > 0) {
 782	        output.note({ title: 'Recalculating project graph...' });
 783	
 784	        const { projectGraphClientResponse, sourceMapResponse } =
 785	          await createProjectGraphAndSourceMapClientResponse();
 786	
 787	        if (
 788	          projectGraphClientResponse.hash !==
 789	            currentProjectGraphClientResponse.hash &&
 790	          sourceMapResponse
 791	        ) {
 792	          if (projectGraphClientResponse.errors?.length > 0) {
 793	            projectGraphClientResponse.errors.forEach((e) => {
 794	              output.error({
