 796	                bodyLines: [e.stack],
 797	              });
 798	            });
 799	            output.warn({
 800	              title: `${
 801	                projectGraphClientResponse.errors.length > 1
 802	                  ? `${projectGraphClientResponse.errors.length} errors`
 803	                  : `An error`
 804	              } occured while processing the project graph. Showing partial graph.`,
 805	            });
 806	          }
 807	          output.note({ title: 'Graph changes updated.' });
 808	
 809	          currentProjectGraphClientResponse = projectGraphClientResponse;
 810	          currentSourceMapsClientResponse = sourceMapResponse;
 811	        } else {
 812	          output.note({ title: 'No graph changes found.' });
 813	        }
 814	      }
 815	    }, 500)
 816	  );
