 819	async function createProjectGraphAndSourceMapClientResponse(
 820	  affected: string[] = []
 821	): Promise<{
 822	  projectGraphClientResponse: ProjectGraphClientResponse;
 823	  sourceMapResponse: ConfigurationSourceMaps;
 824	}> {
 825	  performance.mark('project graph watch calculation:start');
 826	
 827	  let projectGraph: ProjectGraph;
 828	  let sourceMaps: ConfigurationSourceMaps;
 829	  let isPartial = false;
 830	  let errors: GraphError[] | undefined;
 831	  let connectedToCloud: boolean | undefined;
 832	  let disabledTaskSyncGenerators: string[] | undefined;
 833	  try {
 834	    const projectGraphAndSourceMaps =
 835	      await createProjectGraphAndSourceMapsAsync({ exitOnError: false });
 836	    projectGraph = projectGraphAndSourceMaps.projectGraph;
 837	    sourceMaps = projectGraphAndSourceMaps.sourceMaps;
 838	
 839	    const nxJson = readNxJson();
 840	    connectedToCloud = isNxCloudUsed(nxJson);
 841	    disabledTaskSyncGenerators = nxJson.sync?.disabledTaskSyncGenerators;
 842	  } catch (e) {
 843	    if (e instanceof ProjectGraphError) {
 844	      projectGraph = e.getPartialProjectGraph();
 845	      sourceMaps = e.getPartialSourcemaps();
 846	      errors = e.getErrors().map((e) => ({
 847	        message: e.message,
 848	        stack: e.stack,
 849	        cause: e.cause,
 850	        name: e.name,
 851	        pluginName: (e as any).pluginName,
 852	        fileName:
 853	          (e as any).file ?? (e.cause as any)?.errors?.[0]?.location?.file,
 854	      }));
 855	      isPartial = true;
 856	    }
 857	
 858	    if (!projectGraph) {
 859	      handleProjectGraphError({ exitOnError: true }, e);
 860	    }
 861	  }
 862	
 863	  let graph = pruneExternalNodes(projectGraph);
 864	  let fileMap: ProjectFileMap | undefined =
 865	    readFileMapCache()?.fileMap.projectFileMap;
 866	  performance.mark('project graph watch calculation:end');
 867	  performance.mark('project graph response generation:start');
 868	
 869	  const layout = workspaceLayout();
 870	  const projects: ProjectGraphProjectNode[] = Object.values(graph.nodes);
 871	  const dependencies = graph.dependencies;
 872	
 873	  const hasher = createHash('sha256');
 874	  hasher.update(
 875	    JSON.stringify({
 876	      layout,
 877	      projects,
 878	      dependencies,
 879	      sourceMaps,
 880	      errors,
 881	      connectedToCloud,
 882	      disabledTaskSyncGenerators,
 883	    })
 884	  );
 885	
 886	  const hash = hasher.digest('hex');
 887	
 888	  performance.mark('project graph response generation:end');
 889	
 890	  performance.measure(
 891	    'project graph watch calculation',
 892	    'project graph watch calculation:start',
 893	    'project graph watch calculation:end'
 894	  );
 895	
 896	  performance.measure(
 897	    'project graph response generation',
 898	    'project graph response generation:start',
 899	    'project graph response generation:end'
 900	  );
 901	
 902	  return {
 903	    projectGraphClientResponse: {
 904	      ...currentProjectGraphClientResponse,
 905	      hash,
 906	      layout,
 907	      projects,
 908	      dependencies,
 909	      affected,
 910	      fileMap,
 911	      isPartial,
 912	      errors,
 913	      connectedToCloud,
 914	      disabledTaskSyncGenerators,
 915	    },
 916	    sourceMapResponse: sourceMaps,
