 939	  performance.mark('task graph generation:end');
 940	
 941	  const planner = new HashPlanner(
 942	    nxJson,
 943	    transferProjectGraph(transformProjectGraphForRust(graph))
 944	  );
 945	  performance.mark('task hash plan generation:start');
 946	  const plans: Record<string, string[]> = {};
 947	  for (const individualTaskGraph of Object.values(taskGraphs.taskGraphs)) {
 948	    for (const task of Object.values(individualTaskGraph.tasks)) {
 949	      if (plans[task.id]) {
