1110	function expandInputs(
1111	  inputs: string[],
1112	  project: ProjectGraphProjectNode,
1113	  allWorkspaceFiles: FileData[],
1114	  depGraphClientResponse: ProjectGraphClientResponse
1115	): Record<string, string[]> {
1116	  const projectNames = depGraphClientResponse.projects.map((p) => p.name);
1117	
1118	  const workspaceRootInputs: string[] = [];
1119	  const projectRootInputs: string[] = [];
1120	  const externalInputs: string[] = [];
1121	  const otherInputs: string[] = [];
1122	  inputs.forEach((input) => {
1123	    // grouped workspace inputs look like workspace:[pattern,otherPattern]
1124	    if (input.startsWith('workspace:[')) {
1125	      const inputs = input.substring(11, input.length - 1).split(',');
1126	      workspaceRootInputs.push(...inputs);
1127	      return;
1128	    }
1129	    const maybeProjectName = input.split(':')[0];
1130	    if (projectNames.includes(maybeProjectName)) {
1131	      projectRootInputs.push(input);
1132	      return;
1133	    }
1134	    if (
1135	      input === 'ProjectConfiguration' ||
1136	      input === 'TsConfig' ||
1137	      input === 'AllExternalDependencies'
1138	    ) {
1139	      otherInputs.push(input);
1140	      return;
1141	    }
1142	    // there shouldn't be any other imports in here, but external ones are always going to have a modifier in front
1143	    if (input.includes(':')) {
1144	      externalInputs.push(input);
1145	      return;
1146	    }
1147	  });
1148	
1149	  const workspaceRootsExpanded: string[] = getExpandedWorkspaceRoots(
1150	    workspaceRootInputs,
1151	    allWorkspaceFiles
1152	  );
1153	
1154	  const otherInputsExpanded = otherInputs.map((input) => {
1155	    if (input === 'TsConfig') {
1156	      return relative(workspaceRoot, getRootTsConfigPath());
1157	    }
1158	    if (input === 'ProjectConfiguration') {
1159	      return depGraphClientResponse.fileMap[project.name].find(
1160	        (file) =>
1161	          file.file === `${project.data.root}/project.json` ||
1162	          file.file === `${project.data.root}/package.json`
1163	      ).file;
1164	    }
1165	
1166	    return input;
1167	  });
1168	
1169	  const projectRootsExpanded = projectRootInputs
1170	    .map((input) => {
1171	      const fileSetProjectName = input.split(':')[0];
1172	      const fileSetProject = depGraphClientResponse.projects.find(
1173	        (p) => p.name === fileSetProjectName
1174	      );
1175	      const fileSets = input.replace(`${fileSetProjectName}:`, '').split(',');
1176	
1177	      const projectInputExpanded = {
1178	        [fileSetProject.name]: filterUsingGlobPatterns(
1179	          fileSetProject.data.root,
1180	          depGraphClientResponse.fileMap[fileSetProject.name],
1181	          fileSets
1182	        ).map((f) => f.file),
1183	      };
1184	
1185	      return projectInputExpanded;
1186	    })
1187	    .reduce((curr, acc) => {
1188	      for (let key in curr) {
1189	        acc[key] = curr[key];
1190	      }
1191	      return acc;
1192	    }, {});
1193	
1194	  return {
1195	    general: [...workspaceRootsExpanded, ...otherInputsExpanded],
1196	    ...projectRootsExpanded,
1197	    external: externalInputs,
1198	  };
1199	}
