1201	/**
1202	 * The data type that `nx graph --file graph.json` or `nx build --graph graph.json` contains
1203	 */
1204	export interface GraphJson {
1205	  /**
1206	   * A graph of tasks populated with `nx build --graph`
1207	   */
1208	  tasks?: TaskGraph;
1209	  /**
1210	   * The plans for hashing a task in the task graph
1211	   */
1212	  taskPlans?: Record<string, string[]>;
1213	  /**
1214	   * The project graph
1215	   */
1216	  graph: ProjectGraph;
1217	}
1218	
1219	function getExpandedWorkspaceRoots(
1220	  workspaceRootInputs: string[],
1221	  allWorkspaceFiles: FileData[]
1222	) {
1223	  const workspaceRootsExpanded: string[] = [];
1224	  const negativeWRPatterns = [];
1225	  const positiveWRPatterns = [];
1226	  for (const fileset of workspaceRootInputs) {
1227	    if (fileset.startsWith('!')) {
1228	      negativeWRPatterns.push(fileset.substring(17));
1229	    } else {
1230	      positiveWRPatterns.push(fileset.substring(16));
1231	    }
1232	  }
1233	  for (const pattern of positiveWRPatterns) {
1234	    const matchingFile = allWorkspaceFiles.find((t) => t.file === pattern);
1235	    if (
1236	      matchingFile &&
1237	      !negativeWRPatterns.some((p) => minimatch(matchingFile.file, p))
1238	    ) {
1239	      workspaceRootsExpanded.push(matchingFile.file);
1240	    } else {
1241	      allWorkspaceFiles
1242	        .filter(
1243	          (f) =>
1244	            minimatch(f.file, pattern) &&
1245	            !negativeWRPatterns.some((p) => minimatch(f.file, p))
1246	        )
1247	        .forEach((f) => {
1248	          workspaceRootsExpanded.push(f.file);
1249	        });
1250	    }
1251	  }
1252	  workspaceRootsExpanded.sort();
1253	  return workspaceRootsExpanded;
1254	}
1255	
1256	async function createJsonOutput(
1257	  prunedGraph: ProjectGraph,
1258	  rawGraph: ProjectGraph,
1259	  projects: string[],
1260	  targets?: string[]
1261	): Promise<GraphJson> {
1262	  const response: GraphJson = {
1263	    graph: prunedGraph,
1264	  };
1265	
1266	  if (targets?.length) {
1267	    const taskGraph = createTaskGraph(
1268	      rawGraph,
1269	      {},
1270	      projects,
1271	      targets,
1272	      undefined,
1273	      {}
1274	    );
1275	
1276	    const hasher = createTaskHasher(rawGraph, readNxJson());
1277	    let tasks = Object.values(taskGraph.tasks);
1278	    const hashes = await hasher.hashTasks(tasks, taskGraph);
1279	    response.tasks = taskGraph;
1280	    response.taskPlans = tasks.reduce((acc, task, index) => {
1281	      acc[task.id] = Object.keys(hashes[index].details.nodes).sort();
1282	      return acc;
1283	    }, {});
1284	  }
1285	
1286	  return response;
1287	}
1288	
1289	function getHelpTextFromTarget(
1290	  projectName: string,
1291	  targetName: string
1292	): string {
1293	  if (!projectName) throw new Error(`Missing project`);
1294	  if (!targetName) throw new Error(`Missing target`);
1295	
1296	  const project = currentProjectGraphClientResponse.projects?.find(
1297	    (p) => p.name === projectName
1298	  );
1299	  if (!project) throw new Error(`Cannot find project ${projectName}`);
1300	
1301	  const target = project.data.targets[targetName];
1302	  if (!target) throw new Error(`Cannot find target ${targetName}`);
1303	
1304	  const command = target.metadata?.help?.command;
1305	  if (!command)
1306	    throw new Error(`No help command found for ${projectName}:${targetName}`);
1307	
1308	  return execSync(command, {
1309	    cwd: target.options?.cwd ?? workspaceRoot,
1310	    windowsHide: false,
1311	  }).toString();
1312	}
