  1	import { output } from '../../utils/output';
  2	import { readNxJson } from '../../config/configuration';
  3	import { FsTree, flushChanges } from '../../generators/tree';
  4	import {
  5	  connectToNxCloud,
  6	  ConnectToNxCloudOptions,
  7	} from '../../nx-cloud/generators/connect-to-nx-cloud/connect-to-nx-cloud';
  8	import { createNxCloudOnboardingURL } from '../../nx-cloud/utilities/url-shorten';
  9	import { isNxCloudUsed } from '../../utils/nx-cloud-utils';
 10	import { runNxSync } from '../../utils/child-process';
 11	import { NxJsonConfiguration } from '../../config/nx-json';
 12	import { NxArgs } from '../../utils/command-line-utils';
 13	import {
 14	  MessageKey,
 15	  MessageOptionKey,
 16	  recordStat,
 17	  messages,
 18	} from '../../utils/ab-testing';
 19	import { nxVersion } from '../../utils/versions';
 20	import { workspaceRoot } from '../../utils/workspace-root';
 21	import chalk = require('chalk');
 22	import * as ora from 'ora';
 23	import * as open from 'open';
 24	
 25	export function onlyDefaultRunnerIsUsed(nxJson: NxJsonConfiguration) {
 26	  const defaultRunner = nxJson.tasksRunnerOptions?.default?.runner;
 27	
 28	  if (!defaultRunner) {
 29	    // No tasks runner options OR no default runner defined:
 30	    // - If access token defined, uses cloud runner
 31	    // - If no access token defined, uses default
 32	    return (
 33	      !(nxJson.nxCloudAccessToken ?? process.env.NX_CLOUD_ACCESS_TOKEN) &&
 34	      !nxJson.nxCloudId
 35	    );
 36	  }
 37	
 38	  return defaultRunner === 'nx/tasks-runners/default';
 39	}
 40	
 41	export async function connectToNxCloudIfExplicitlyAsked(
 42	  opts: NxArgs
 43	): Promise<void> {
 44	  if (opts['cloud'] === true) {
 45	    const nxJson = readNxJson();
 46	    if (!onlyDefaultRunnerIsUsed(nxJson)) return;
 47	
 48	    output.log({
 49	      title: '--cloud requires the workspace to be connected to Nx Cloud.',
 50	    });
 51	    runNxSync(`connect-to-nx-cloud`, {
 52	      stdio: [0, 1, 2],
 53	    });
 54	    output.success({
 55	      title: 'Your workspace has been successfully connected to Nx Cloud.',
 56	    });
 57	    process.exit(0);
 58	  }
 59	}
 60	
 61	export async function connectWorkspaceToCloud(
 62	  options: ConnectToNxCloudOptions,
 63	  directory = workspaceRoot
 64	) {
 65	  const tree = new FsTree(directory, false, 'connect-to-nx-cloud');
 66	  const accessToken = await connectToNxCloud(tree, options);
 67	  tree.lock();
 68	  flushChanges(directory, tree.listChanges());
 69	  return accessToken;
 70	}
 71	
 72	export async function connectToNxCloudCommand(
 73	  options: { generateToken?: boolean },
 74	  command?: string
 75	): Promise<boolean> {
 76	  const nxJson = readNxJson();
 77	
 78	  const installationSource = process.env.NX_CONSOLE
 79	    ? 'nx-console'
 80	    : 'nx-connect';
 81	
 82	  if (isNxCloudUsed(nxJson)) {
 83	    const token =
 84	      process.env.NX_CLOUD_ACCESS_TOKEN ||
 85	      nxJson.nxCloudAccessToken ||
 86	      nxJson.nxCloudId;
 87	    if (!token) {
 88	      throw new Error(
 89	        `Unable to authenticate. If you are connecting to Nx Cloud locally, set Nx Cloud ID in nx.json. If you are connecting in a CI context, either define accessToken in nx.json or set the NX_CLOUD_ACCESS_TOKEN env variable.`
 90	      );
 91	    }
 92	    const connectCloudUrl = await createNxCloudOnboardingURL(
 93	      installationSource,
 94	      token,
 95	      options?.generateToken !== true
 96	    );
 97	    output.log({
 98	      title: ' This workspace already has Nx Cloud set up',
 99	      bodyLines: [
100	        'If you have not done so already, connect your workspace to your Nx Cloud account with the following URL:',
101	        '',
102	        `${connectCloudUrl}`,
103	      ],
104	    });
105	
106	    return false;
107	  }
108	  const token = await connectWorkspaceToCloud({
109	    generateToken: options?.generateToken,
110	    installationSource: command ?? installationSource,
111	  });
112	
113	  const connectCloudUrl = await createNxCloudOnboardingURL(
114	    'nx-connect',
115	    token,
116	    options?.generateToken !== true
117	  );
118	  try {
119	    const cloudConnectSpinner = ora(
120	      `Opening Nx Cloud ${connectCloudUrl} in your browser to connect your workspace.`
121	    ).start();
122	    await sleep(2000);
123	    await open(connectCloudUrl);
124	    cloudConnectSpinner.succeed();
125	  } catch (e) {
126	    output.note({
127	      title: `Your Nx Cloud workspace is ready.`,
128	      bodyLines: [
129	        `To claim it, connect it to your Nx Cloud account:`,
130	        `- Go to the following URL to connect your workspace to Nx Cloud:`,
131	        '',
132	        `${connectCloudUrl}`,
133	      ],
134	    });
135	  }
136	
137	  return true;
138	}
139	
140	function sleep(ms: number) {
141	  return new Promise((resolve) => setTimeout(resolve, ms));
142	}
143	
144	export async function connectExistingRepoToNxCloudPrompt(
145	  command = 'init',
146	  key: MessageKey = 'setupNxCloud'
147	): Promise<boolean> {
148	  const res = await nxCloudPrompt(key).then(
149	    (value: MessageOptionKey) => value === 'yes'
150	  );
151	  await recordStat({
152	    command,
153	    nxVersion,
154	    useCloud: res,
155	    meta: messages.codeOfSelectedPromptMessage(key),
156	  });
157	  return res;
158	}
159	
