  2	import { Module } from 'module';
  3	import { clean, coerce, gt } from 'semver';
  4	
  5	import { installPackagesTask } from '../tasks/install-packages-task';
  6	import { dirSync } from 'tmp';
  7	import { join } from 'path';
  8	import {
  9	  detectPackageManager,
 10	  GeneratorCallback,
 11	  getPackageManagerCommand,
 12	  getPackageManagerVersion,
 13	  PackageManager,
 14	  readJson,
 15	  Tree,
 16	  updateJson,
 17	  workspaceRoot,
 18	} from 'nx/src/devkit-exports';
 19	import { createTempNpmDirectory } from 'nx/src/devkit-internals';
 20	import { writeFileSync } from 'fs';
 21	
 22	const UNIDENTIFIED_VERSION = 'UNIDENTIFIED_VERSION';
 23	const NON_SEMVER_TAGS = {
 24	  '*': 2,
 25	  [UNIDENTIFIED_VERSION]: 2,
 26	  next: 1,
 27	  latest: 0,
 28	  previous: -1,
 29	  legacy: -2,
 30	};
 31	
 32	function filterExistingDependencies(
 33	  dependencies: Record<string, string>,
 34	  existingAltDependencies: Record<string, string>
 35	) {
 36	  if (!existingAltDependencies) {
 37	    return dependencies;
 38	  }
 39	
 40	  return Object.keys(dependencies ?? {})
 41	    .filter((d) => !existingAltDependencies[d])
 42	    .reduce((acc, d) => ({ ...acc, [d]: dependencies[d] }), {});
 43	}
 44	
 45	function cleanSemver(version: string) {
 46	  return clean(version) ?? coerce(version);
 47	}
 48	
 49	function isIncomingVersionGreater(
 50	  incomingVersion: string,
 51	  existingVersion: string
 52	) {
 53	  // if version is in the format of "latest", "next" or similar - keep it, otherwise try to parse it
 54	  const incomingVersionCompareBy =
 55	    incomingVersion in NON_SEMVER_TAGS
 56	      ? incomingVersion
 57	      : cleanSemver(incomingVersion)?.toString() ?? UNIDENTIFIED_VERSION;
 58	  const existingVersionCompareBy =
 59	    existingVersion in NON_SEMVER_TAGS
 60	      ? existingVersion
 61	      : cleanSemver(existingVersion)?.toString() ?? UNIDENTIFIED_VERSION;
 62	
 63	  if (
 64	    incomingVersionCompareBy in NON_SEMVER_TAGS &&
 65	    existingVersionCompareBy in NON_SEMVER_TAGS
 66	  ) {
 67	    return (
 68	      NON_SEMVER_TAGS[incomingVersionCompareBy] >
 69	      NON_SEMVER_TAGS[existingVersionCompareBy]
 70	    );
 71	  }
 72	
 73	  if (
 74	    incomingVersionCompareBy in NON_SEMVER_TAGS ||
 75	    existingVersionCompareBy in NON_SEMVER_TAGS
 76	  ) {
 77	    return true;
 78	  }
 79	
 80	  return gt(cleanSemver(incomingVersion), cleanSemver(existingVersion));
 81	}
 82	
 83	function updateExistingAltDependenciesVersion(
 84	  dependencies: Record<string, string>,
 85	  existingAltDependencies: Record<string, string>
 86	) {
 87	  return Object.keys(existingAltDependencies || {})
 88	    .filter((d) => {
 89	      if (!dependencies[d]) {
 90	        return false;
 91	      }
 92	
 93	      const incomingVersion = dependencies[d];
 94	      const existingVersion = existingAltDependencies[d];
 95	      return isIncomingVersionGreater(incomingVersion, existingVersion);
 96	    })
 97	    .reduce((acc, d) => ({ ...acc, [d]: dependencies[d] }), {});
 98	}
 99	
100	function updateExistingDependenciesVersion(
101	  dependencies: Record<string, string>,
102	  existingDependencies: Record<string, string> = {}
103	) {
104	  return Object.keys(dependencies)
105	    .filter((d) => {
106	      if (!existingDependencies[d]) {
107	        return true;
108	      }
109	
110	      const incomingVersion = dependencies[d];
111	      const existingVersion = existingDependencies[d];
112	
113	      return isIncomingVersionGreater(incomingVersion, existingVersion);
114	    })
115	    .reduce((acc, d) => ({ ...acc, [d]: dependencies[d] }), {});
116	}
117	
118	/**
119	 * Add Dependencies and Dev Dependencies to package.json
120	 *
121	 * For example:
122	 * ```typescript
123	 * addDependenciesToPackageJson(tree, { react: 'latest' }, { jest: 'latest' })
124	 * ```
125	 * This will **add** `react` and `jest` to the dependencies and devDependencies sections of package.json respectively.
126	 *
127	 * @param tree Tree representing file system to modify
128	 * @param dependencies Dependencies to be added to the dependencies section of package.json
129	 * @param devDependencies Dependencies to be added to the devDependencies section of package.json
130	 * @param packageJsonPath Path to package.json
131	 * @param keepExistingVersions If true, prevents existing dependencies from being bumped to newer versions
132	 * @returns Callback to install dependencies only if necessary, no-op otherwise
133	 */
134	export function addDependenciesToPackageJson(
135	  tree: Tree,
136	  dependencies: Record<string, string>,
137	  devDependencies: Record<string, string>,
138	  packageJsonPath: string = 'package.json',
139	  keepExistingVersions?: boolean
140	): GeneratorCallback {
141	  const currentPackageJson = readJson(tree, packageJsonPath);
142	
143	  /** Dependencies to install that are not met in dev dependencies */
144	  let filteredDependencies = filterExistingDependencies(
145	    dependencies,
146	    currentPackageJson.devDependencies
147	  );
148	  /** Dev dependencies to install that are not met in dependencies */
149	  let filteredDevDependencies = filterExistingDependencies(
150	    devDependencies,
151	    currentPackageJson.dependencies
152	  );
153	
154	  // filtered dependencies should consist of:
155	  // - dependencies of the same type that are not present
156	  // by default, filtered dependencies also include these (unless keepExistingVersions is true):
157	  // - dependencies of the same type that have greater version
158	  // - specified dependencies of the other type that have greater version and are already installed as current type
159	  filteredDependencies = {
160	    ...updateExistingDependenciesVersion(
161	      filteredDependencies,
162	      currentPackageJson.dependencies
163	    ),
164	    ...updateExistingAltDependenciesVersion(
165	      devDependencies,
166	      currentPackageJson.dependencies
167	    ),
168	  };
169	  filteredDevDependencies = {
170	    ...updateExistingDependenciesVersion(
171	      filteredDevDependencies,
172	      currentPackageJson.devDependencies
173	    ),
174	    ...updateExistingAltDependenciesVersion(
175	      dependencies,
176	      currentPackageJson.devDependencies
177	    ),
178	  };
179	
180	  if (keepExistingVersions) {
181	    filteredDependencies = removeExistingDependencies(
182	      filteredDependencies,
183	      currentPackageJson.dependencies
184	    );
185	    filteredDevDependencies = removeExistingDependencies(
186	      filteredDevDependencies,
187	      currentPackageJson.devDependencies
188	    );
189	  } else {
