191	      filteredDependencies,
192	      currentPackageJson.dependencies
193	    );
194	    filteredDevDependencies = removeLowerVersions(
195	      filteredDevDependencies,
196	      currentPackageJson.devDependencies
197	    );
198	  }
199	
200	  if (
201	    requiresAddingOfPackages(
202	      currentPackageJson,
203	      filteredDependencies,
204	      filteredDevDependencies
205	    )
206	  ) {
207	    updateJson(tree, packageJsonPath, (json) => {
208	      json.dependencies = {
209	        ...(json.dependencies || {}),
210	        ...filteredDependencies,
211	      };
212	
213	      json.devDependencies = {
214	        ...(json.devDependencies || {}),
215	        ...filteredDevDependencies,
216	      };
217	
218	      json.dependencies = sortObjectByKeys(json.dependencies);
219	      json.devDependencies = sortObjectByKeys(json.devDependencies);
220	
221	      return json;
222	    });
223	
224	    return (): void => {
225	      installPackagesTask(tree);
226	    };
227	  }
228	  return () => {};
229	}
