439	 * ```typescript
440	 * ensurePackage('@nx/jest', nxVersion)
441	 * ```
442	 *
443	 * @param pkg the package to install and require
444	 * @param version the version to install if the package doesn't exist already
445	 */
446	export function ensurePackage<T extends any = any>(
447	  pkg: string,
448	  version: string
449	): T;
450	export function ensurePackage<T extends any = any>(
451	  pkgOrTree: string | Tree,
452	  requiredVersionOrPackage: string,
453	  maybeRequiredVersion?: string,
454	  _?: never
455	): T {
456	  let pkg: string;
457	  let requiredVersion: string;
458	  if (typeof pkgOrTree === 'string') {
459	    pkg = pkgOrTree;
460	    requiredVersion = requiredVersionOrPackage;
461	  } else {
462	    // Old Signature
463	    pkg = requiredVersionOrPackage;
464	    requiredVersion = maybeRequiredVersion;
465	  }
466	
467	  if (packageMapCache.has(pkg)) {
468	    return packageMapCache.get(pkg) as T;
469	  }
470	
471	  try {
472	    return require(pkg);
473	  } catch (e) {
474	    if (e.code === 'ERR_REQUIRE_ESM') {
475	      // The package is installed, but is an ESM package.
476	      // The consumer of this function can import it as needed.
477	      return null;
478	    } else if (e.code !== 'MODULE_NOT_FOUND') {
479	      throw e;
480	    }
481	  }
482	
483	  if (process.env.NX_DRY_RUN && process.env.NX_DRY_RUN !== 'false') {
484	    throw new Error(
485	      'NOTE: This generator does not support --dry-run. If you are running this in Nx Console, it should execute fine once you hit the "Generate" button.\n'
486	    );
487	  }
488	
489	  const { dir: tempDir } = createTempNpmDirectory?.() ?? {
490	    dir: dirSync().name,
491	  };
492	
493	  console.log(`Fetching ${pkg}...`);
494	  const packageManager = detectPackageManager();
495	  const isVerbose = process.env.NX_VERBOSE_LOGGING === 'true';
496	  generatePackageManagerFiles(tempDir, packageManager);
497	  const preInstallCommand = getPackageManagerCommand(packageManager).preInstall;
498	  if (preInstallCommand) {
499	    // ensure package.json and repo in tmp folder is set to a proper package manager state
500	    execSync(preInstallCommand, {
501	      cwd: tempDir,
502	      stdio: isVerbose ? 'inherit' : 'ignore',
503	      windowsHide: false,
504	    });
505	  }
506	  let addCommand = getPackageManagerCommand(packageManager).addDev;
507	  if (packageManager === 'pnpm') {
508	    addCommand = 'pnpm add -D'; // we need to ensure that we are not using workspace command
509	  }
510	
511	  execSync(`${addCommand} ${pkg}@${requiredVersion}`, {
512	    cwd: tempDir,
513	    stdio: isVerbose ? 'inherit' : 'ignore',
514	    windowsHide: false,
515	  });
516	
517	  addToNodePath(join(workspaceRoot, 'node_modules'));
518	  addToNodePath(join(tempDir, 'node_modules'));
519	
520	  // Re-initialize the added paths into require
521	  (Module as any)._initPaths();
522	
523	  try {
524	    const result = require(require.resolve(pkg, {
525	      paths: [tempDir],
526	    }));
527	
528	    packageMapCache.set(pkg, result);
529	
530	    return result;
531	  } catch (e) {
532	    if (e.code === 'ERR_REQUIRE_ESM') {
533	      // The package is installed, but is an ESM package.
534	      // The consumer of this function can import it as needed.
535	      packageMapCache.set(pkg, null);
536	      return null;
537	    }
538	    throw e;
539	  }
540	}
