542	/**
543	 * Generates necessary files needed for the package manager to work
544	 * and for the node_modules to be accessible.
545	 */
546	function generatePackageManagerFiles(
547	  root: string,
548	  packageManager: PackageManager = detectPackageManager()
549	) {
550	  const [pmMajor] = getPackageManagerVersion(packageManager).split('.');
551	  switch (packageManager) {
552	    case 'yarn':
553	      if (+pmMajor >= 2) {
554	        writeFileSync(
555	          join(root, '.yarnrc.yml'),
556	          'nodeLinker: node-modules\nenableScripts: false'
557	        );
558	      }
559	      break;
560	  }
561	}
