563	function addToNodePath(dir: string) {
564	  // NODE_PATH is a delimited list of paths.
565	  // The delimiter is different for windows.
566	  const delimiter = require('os').platform() === 'win32' ? ';' : ':';
567	
568	  const paths = process.env.NODE_PATH
569	    ? process.env.NODE_PATH.split(delimiter)
570	    : [];
571	
572	  // The path is already in the node path
573	  if (paths.includes(dir)) {
574	    return;
575	  }
576	
577	  // Add the tmp path
578	  paths.push(dir);
579	
580	  // Update the env variable.
581	  process.env.NODE_PATH = paths.join(delimiter);
582	}
