  1	import { exec } from 'child_process';
  2	import { existsSync } from 'fs';
  3	import * as ora from 'ora';
  4	import * as yargsParser from 'yargs-parser';
  5	import { readNxJson, type NxJsonConfiguration } from '../../config/nx-json';
  6	import { runNxAsync } from '../../utils/child-process';
  7	import { writeJsonFile } from '../../utils/fileutils';
  8	import { logger } from '../../utils/logger';
  9	import { output } from '../../utils/output';
 10	import {
 11	  detectPackageManager,
 12	  getPackageManagerCommand,
 13	  getPackageManagerVersion,
 14	} from '../../utils/package-manager';
 15	import { handleErrors } from '../../utils/handle-errors';
 16	import { nxVersion } from '../../utils/versions';
 17	import { workspaceRoot } from '../../utils/workspace-root';
 18	import type { AddOptions } from './command-object';
 19	import { normalizeVersionForNxJson } from '../init/implementation/dot-nx/add-nx-scripts';
 20	import { gte } from 'semver';
 21	import {
 22	  runPluginInitGenerator,
 23	  getFailedToInstallPluginErrorMessages,
 24	} from '../init/configure-plugins';
 25	
 26	export function addHandler(options: AddOptions): Promise<number> {
 27	  return handleErrors(options.verbose, async () => {
 28	    output.addNewline();
 29	
 30	    const [pkgName, version] = parsePackageSpecifier(options.packageSpecifier);
 31	    const nxJson = readNxJson();
 32	
 33	    await installPackage(pkgName, version, nxJson);
 34	    await initializePlugin(pkgName, options, nxJson);
 35	
 36	    output.success({
 37	      title: `Package ${pkgName} added successfully.`,
 38	    });
 39	  });
 40	}
 41	
 42	async function installPackage(
 43	  pkgName: string,
 44	  version: string,
 45	  nxJson: NxJsonConfiguration
 46	): Promise<void> {
 47	  const spinner = ora(`Installing ${pkgName}@${version}...`);
 48	  spinner.start();
 49	
 50	  if (existsSync('package.json')) {
 51	    const pm = detectPackageManager();
 52	    const pmv = getPackageManagerVersion(pm);
 53	    const pmc = getPackageManagerCommand(pm);
 54	
 55	    // if we explicitly specify latest in yarn berry, it won't resolve the version
 56	    const command =
 57	      pm === 'yarn' && gte(pmv, '2.0.0') && version === 'latest'
 58	        ? `${pmc.addDev} ${pkgName}`
 59	        : `${pmc.addDev} ${pkgName}@${version}`;
 60	    await new Promise<void>((resolve) =>
 61	      exec(
 62	        command,
 63	        {
 64	          windowsHide: false,
 65	        },
 66	        (error, stdout) => {
 67	          if (error) {
 68	            spinner.fail();
 69	            output.addNewline();
 70	            logger.error(stdout);
 71	            output.error({
 72	              title: `Failed to install ${pkgName}. Please check the error above for more details.`,
 73	            });
 74	            process.exit(1);
 75	          }
 76	
 77	          return resolve();
 78	        }
 79	      )
 80	    );
 81	  } else {
 82	    nxJson.installation.plugins ??= {};
 83	    nxJson.installation.plugins[pkgName] = normalizeVersionForNxJson(
 84	      pkgName,
 85	      version
 86	    );
 87	    writeJsonFile('nx.json', nxJson);
 88	
 89	    try {
 90	      await runNxAsync('--help', { silent: true });
 91	    } catch (e) {
 92	      // revert adding the plugin to nx.json
 93	      nxJson.installation.plugins[pkgName] = undefined;
 94	      writeJsonFile('nx.json', nxJson);
 95	
 96	      spinner.fail();
 97	      output.addNewline();
 98	      logger.error(e.message);
 99	      output.error({
100	        title: `Failed to install ${pkgName}. Please check the error above for more details.`,
101	      });
102	      process.exit(1);
103	    }
104	  }
105	
106	  spinner.succeed();
107	}
108	
109	async function initializePlugin(
110	  pkgName: string,
111	  options: AddOptions,
112	  nxJson: NxJsonConfiguration
113	): Promise<void> {
114	  let updatePackageScripts = false;
115	  if (
116	    coreNxPluginVersions.has(pkgName) &&
117	    (options.updatePackageScripts ||
118	      (options.updatePackageScripts === undefined &&
119	        nxJson.useInferencePlugins !== false &&
120	        process.env.NX_ADD_PLUGINS !== 'false'))
121	  ) {
122	    updatePackageScripts = true;
123	  }
124	
125	  const spinner = ora(`Initializing ${pkgName}...`);
126	  spinner.start();
127	
128	  try {
129	    await runPluginInitGenerator(
130	      pkgName,
131	      workspaceRoot,
132	      updatePackageScripts,
133	      options.verbose
134	    );
135	  } catch (e) {
136	    spinner.fail();
137	    output.addNewline();
138	    output.error({
139	      title: `Failed to initialize ${pkgName}`,
140	      bodyLines: getFailedToInstallPluginErrorMessages(e),
141	    });
142	    process.exit(1);
143	  }
144	
145	  spinner.succeed();
146	}
147	
148	function parsePackageSpecifier(
149	  packageSpecifier: string
150	): [pkgName: string, version: string] {
151	  const i = packageSpecifier.lastIndexOf('@');
152	
153	  if (i <= 0) {
154	    if (coreNxPluginVersions.has(packageSpecifier)) {
155	      return [packageSpecifier, coreNxPluginVersions.get(packageSpecifier)];
156	    }
157	
158	    return [packageSpecifier, 'latest'];
159	  }
160	
161	  const pkgName = packageSpecifier.substring(0, i);
162	  const version = packageSpecifier.substring(i + 1);
163	
164	  return [pkgName, version];
165	}
166	
167	export const coreNxPluginVersions = (
168	  require('../../../package.json') as typeof import('../../../package.json')
169	)['nx-migrations'].packageGroup.reduce(
170	  (map, entry) => {
171	    const packageName = typeof entry === 'string' ? entry : entry.package;
172	    const version = typeof entry === 'string' ? nxVersion : entry.version;
173	    return map.set(packageName, version);
174	  },
175	  // Package Name -> Desired Version
176	  new Map<string, string>()
177	);
