 41	      console.log(`${chalk.green('CREATE')} ${f.path}`);
 42	    } else if (f.type === 'UPDATE') {
 43	      console.log(`${chalk.white('UPDATE')} ${f.path}`);
 44	    } else if (f.type === 'DELETE') {
 45	      console.log(`${chalk.yellow('DELETE')} ${f.path}`);
 46	    }
