 50	async function promptForCollection(
 51	  generatorName: string,
 52	  interactive: boolean,
 53	  projectsConfiguration: ProjectsConfigurations
 54	): Promise<string> {
 55	  const localPlugins = await getLocalWorkspacePlugins(
 56	    projectsConfiguration,
 57	    readNxJson()
 58	  );
 59	
 60	  const installedCollections = Array.from(
 61	    new Set(findInstalledPlugins().map((x) => x.name))
 62	  );
 63	
 64	  const choicesMap = new Set<string>();
 65	
 66	  const deprecatedChoices = new Set<string>();
 67	
 68	  for (const collectionName of installedCollections) {
