  1	use crate::native::ide::detection::{SupportedEditor, get_current_editor};
  2	use crate::native::logger::enable_logger;
  3	use napi::Error;
  4	use std::process::Command;
  5	use tracing::{debug, info, trace};
  6	
  7	const NX_CONSOLE_EXTENSION_ID: &str = "nrwl.angular-console";
  8	
  9	fn install_nx_console(command: &str) -> Result<(), Error> {
 10	    debug!(
 11	        "Attempting to install Nx Console extension with: {} --install-extension {}",
 12	        command, NX_CONSOLE_EXTENSION_ID
 13	    );
 14	
 15	    let output = match Command::new(command)
 16	        .arg("--install-extension")
 17	        .arg(NX_CONSOLE_EXTENSION_ID)
 18	        .output()
 19	    {
 20	        Ok(output) => output,
 21	        Err(e) => match e.kind() {
 22	            std::io::ErrorKind::NotFound => {
 23	                debug!(
 24	                    "Command '{}' not found, skipping extension installation",
 25	                    command
 26	                );
 27	                return Ok(());
 28	            }
 29	            _ => {
 30	                debug!("Failed to execute command: {}", e);
 31	                return Err(Error::from_reason(format!(
 32	                    "Failed to run command '{}': {}",
 33	                    command, e
 34	                )));
 35	            }
 36	        },
 37	    };
 38	
 39	    let stdout = String::from_utf8_lossy(&output.stdout);
 40	    let stderr = String::from_utf8_lossy(&output.stderr);
 41	
 42	    if output.status.success() {
 43	        if stdout.contains("already installed") {
 44	            debug!("Nx Console extension is already installed");
 45	        } else {
 46	            info!("Successfully installed Nx Console extension");
 47	        }
 48	        trace!("Command output: {}", stdout.trim());
 49	        return Ok(());
 50	    }
 51	
 52	    // Check for "already installed" message in stdout or stderr
 53	    let combined_output = format!("{} {}", stdout, stderr);
 54	    if combined_output.contains("already installed") {
 55	        debug!("Nx Console extension is already installed");
 56	        return Ok(());
 57	    }
 58	
 59	    // Log the error output for debugging
 60	    debug!("Command failed with status: {:?}", output.status);
 61	    if !stdout.is_empty() {
 62	        trace!("Command stdout: {}", stdout.trim());
 63	    }
 64	    if !stderr.is_empty() {
 65	        trace!("Command stderr: {}", stderr.trim());
 66	    }
 67	
 68	    // Command failed but this is OK - we don't want to crash Nx
 69	    Ok(())
 70	}
 71	
 72	#[napi]
 73	pub fn ensure_nx_console_installed() {
 74	    enable_logger();
 75	
 76	    // Check if installation should be skipped
 77	    let skip_install = std::env::var("NX_SKIP_VSCODE_EXTENSION_INSTALL")
 78	        .map(|v| v == "true")
 79	        .unwrap_or(false);
 80	
 81	    if skip_install {
 82	        debug!("Nx Console extension installation disabled via NX_SKIP_VSCODE_EXTENSION_INSTALL");
 83	        return;
 84	    }
 85	
 86	    // Use the sophisticated editor detection from nx_console
 87	    let current_editor = get_current_editor();
 88	    debug!("Detected editor: {:?}", current_editor);
