import { existsSync, mkdirSync, readFileSync, writeFileSync } from 'fs';
import { homedir } from 'os';
import { join } from 'path';

export interface UserConfig {
  idePluginInstallation?: 'always' | 'never' | 'prompt';
}

const USER_CONFIG_DIR = join(homedir(), '.nx');
const USER_CONFIG_FILE = join(USER_CONFIG_DIR, 'config.json');

/**
 * Reads the user's global Nx configuration from ~/.nx/config.json
 */
export function readUserConfig(): UserConfig {
  try {
    if (!existsSync(USER_CONFIG_FILE)) {
      return {};
    }
    const content = readFileSync(USER_CONFIG_FILE, 'utf-8');
    return JSON.parse(content);
  } catch (error) {
    // If config is corrupted or unreadable, return empty config
    return {};
  }
}

/**
 * Writes the user's global Nx configuration to ~/.nx/config.json
 */
export function writeUserConfig(config: UserConfig): void {
  try {
    // Ensure the config directory exists
    if (!existsSync(USER_CONFIG_DIR)) {
      mkdirSync(USER_CONFIG_DIR, { recursive: true });
    }

    writeFileSync(USER_CONFIG_FILE, JSON.stringify(config, null, 2), 'utf-8');
  } catch (error) {
    // Silently fail if we can't write to the user's home directory
    // This ensures Nx continues to work even if filesystem permissions are restrictive
  }
}

/**
 * Updates a specific property in the user config
 */
export function updateUserConfig(updates: Partial<UserConfig>): void {
  const currentConfig = readUserConfig();
  const newConfig = { ...currentConfig, ...updates };
  writeUserConfig(newConfig);
}

/**
 * Gets the user's preference for IDE plugin installation
 * Returns 'prompt' by default if no preference is set
 */
export function getIdePluginInstallationPreference(): 'always' | 'never' | 'prompt' {
  const config = readUserConfig();
  return config.idePluginInstallation || 'prompt';
}

/**
 * Sets the user's preference for IDE plugin installation
 */
export function setIdePluginInstallationPreference(preference: 'always' | 'never' | 'prompt'): void {
  updateUserConfig({ idePluginInstallation: preference });
}

